REPLACE INTO rvs_project_info (project_info_id,template_item_id,font_id,navigator_id,rvs_category_id,charset_id,project_company,project_slogan,project_logo,project_title,project_keyword,project_description,project_domain,check_step,effect_site_enter,duration_site_enter,last_update,layout_template_id,i_pathway,i_mail,i_print,i_favorite,no_slogan,no_company_name,navigator_name_id) VALUES ('d6184fa2706874731e0b50b3802bb08f','bb9fe3b4605cabd67afed412dabfcb2c','1','7c834ed7fe83efd1e091c8caf288658d','17','20','Casa Hogar Los Gorriones','','NOPIC','Casa Hogar Los Gorriones','','','casahogarlosgorriones.org','4',null,null,'1204678068','6e3632261def517e1a8becfa67b500f4','1','1','1','1','0','0','7');
REPLACE INTO rvs_project_page (project_page_id,rvs_usr_id,page_name,custom_page_name,rvs_component_id,page_data,head,page_title,meta_keyword,meta_description,effect_page_enter,duration_page_enter,meta_page_effect,rvs_link_id,target,form_template_id,i_pathway,i_mail,i_print,i_favorite) VALUES ('9c6798e92c606ee2ae98f655292fcdbd','4a9479c2086afe0578d281c45072190b','Los Gorriones','','0','<p>\r\n<table style=\"width: 68.86%; border-collapse: collapse; height: 184px\" bordercolor=\"#000000\" cellspacing=\"0\" cellpadding=\"3\" summary=\"\" border=\"0\">\r\n<tbody>\r\n<tr valign=\"top\">\r\n<td>\r\n<div>&nbsp;<img title=\"\" style=\"width: 150px; height: 194px\" height=\"200\" alt=\"\" width=\"150\" src=\"/3rdparty/rvsitebuilder/project/871add3c9f1716ad9328e77a371e6050/images/Noemi.jpg\" /></div></td>\r\n<td>\r\n<div>&nbsp;<img title=\"\" style=\"width: 251px; height: 194px\" height=\"200\" alt=\"\" width=\"300\" src=\"/3rdparty/rvsitebuilder/project/871add3c9f1716ad9328e77a371e6050/images/_MG_7999.jpg\" /></div></td>\r\n<td>\r\n<div>&nbsp;<img title=\"\" style=\"width: 248px; height: 192px\" height=\"200\" alt=\"\" width=\"250\" src=\"/3rdparty/rvsitebuilder/project/871add3c9f1716ad9328e77a371e6050/images/Nilda.jpg\" /></div></td>\r\n<td>\r\n<div>&nbsp;<img title=\"\" style=\"width: 244px; height: 193px\" height=\"200\" alt=\"\" width=\"285\" src=\"/3rdparty/rvsitebuilder/project/871add3c9f1716ad9328e77a371e6050/images/web.jpg\" /></div></td>\r\n<td>\r\n<div>&nbsp;<img title=\"\" style=\"width: 246px; height: 194px\" height=\"150\" alt=\"\" width=\"200\" src=\"/3rdparty/rvsitebuilder/project/871add3c9f1716ad9328e77a371e6050/images/www.jpg\" /></div></td></tr></tbody></table><br />\r\n<!-- ####### LAYOUT CSS: MODIFY COLOR, FONT AND BOX SIZE HERE BUT DO NOT DELETE. ####### -->\r\n<style type=\"text/css\">\r\n\r\n.type12rightpad01{padding:10px 3px;}\r\n.type12rightpad02{padding:6px 3px;}\r\n.type12bgsilver{ background-color:#E7E7E7;}\r\n.type12bggray{ background-color:#C5C5C5;}\r\n.type12txtbold{\r\n	font-weight:bold;\r\n}\r\n.type12txthead{\r\n	font-weight:bold;\r\n	color:#535353;\r\n}\r\n.type12head{ font-weight:bold;}\r\n.type12linedash{border-bottom:#999999 dashed 1px;}\r\n.type12bgwhite{\r\n	background-color:#FFFFFF;\r\n	color:#535353;\r\n}\r\n.type12txtbody{color:#535353;}</style><!-- ########################### END CSS LAYOUT ###########################  -->\r\n<table id=\"rv_template_layout\" cellspacing=\"0\" cellpadding=\"0\">\r\n<tbody>\r\n<tr>\r\n<td valign=\"top\" align=\"left\">\r\n<table cellspacing=\"0\" cellpadding=\"5\" width=\"100%\">\r\n<tbody>\r\n<tr>\r\n<td valign=\"top\" align=\"left\" width=\"70%\">\r\n<table class=\"type12bgsilver\" cellspacing=\"0\" cellpadding=\"5\" width=\"100%\">\r\n<tbody>\r\n<tr>\r\n<td valign=\"top\"></td>\r\n<td valign=\"top\">\r\n<div class=\"type12txthead\" align=\"justify\"><font size=\"5\">Das Haus der Kinder “Die Spatzen”</font></div>\r\n<p class=\"type12txtbody\" align=\"justify\"><font size=\"3\">Wir befinden uns in Ayacucho, 450 km südlich von <?xml:namespace prefix = st1>\r\n<st1:city>\r\n<st1:place>Lima</st1:place></st1:city>, im Herzen der peruanischen Anden. Ayacucho ist eine der ärmsten Städte in einem der ärmsten Länder. Genau hier formierte sich die terroristische Bewegung des leuchtenden Pfads (Sendero Luminoso), die aus dieser Region in den 80er und 90er Jahren eine der gefährlichsten machte. Heute leben in dieser dennoch so charmanten Stadt ehemalige Bauern, die ihre Dörfer aus Sicherheitsgründen verließen und in der Stadt Schutz vor der terroristischen Bewegung suchten. Hier angekommen, waren sie jedoch in Folge der zunehmenden Bevölkerungsdichte und Armut von einer extremen Arbeitslosigkeit betroffen. Wir sprechen dabei von 80 %. Aber was bedeutet diese Zahl wirklich? Es könnte hilfreich sein, sich ein Bild zu machen: Die Sonne prallt auf die Blechdächer der Häuser und alte Damen verkaufen am Stra&#946;enrand Klee um mit Mühe und Not 75 Cent am Tag zu verdienen. Kaum zu sprechen von den Fahrern der Mototaxen, die 15 Stunden am Tag arbeiten und dennoch nicht die Ausbildung ihrer Kinder finanzieren können. Doch nicht nur die Erwachsenen, auch die Kinder Ayacuchos versuchen sich mit zahlreichen “Geschäftsideen” am Leben zu erhalten. Sie verkaufen alles, was sie finden können, betteln, singen und bieten kleine Dienstleistungen wie Schuhe putzen an. In Folge der Armut und der daraus resultierenden Verzweiflung verwüsten Alkoholismus und Gewalt die Wohnhäuser, zerstören Familien und die Prostitution fällt in die Straßen ein. Das ist nicht\r\n<st1:country-region>\r\n<st1:place>Peru</st1:place></st1:country-region>! Dennoch sind wir hier, denn das goldene Zeitalter der Inka scheint vorbei zu sein und die einzigartige peruanische Kultur gerät in Vergessenheit! Heute ist\r\n<st1:place>\r\n<st1:placename>Peru</st1:placename>\r\n<st1:placename>ein</st1:placename>\r\n<st1:placetype>Land</st1:placetype></st1:place>, in dem sich Schicksale wie diese millionenfach zu Papier bringen lassen. Unter diesen Bedingungen\r\n<st1:city>\r\n<st1:place>leiden</st1:place></st1:city> besonders viele Kinder. Auf Grund der extremen Armut sehen viele Eltern die einzige Chance für das Überleben ihrer Kinder darin, sie wegzugeben. Oft sind die Kinder ernsthaft unterernährt und krank, haben traumatische Erfahrungen machen müssen und so gut wie keine Erziehung bezüglich der persönlichen Hygiene genossen. Auffanglager und Kinderheime sind jedoch selten und die, die es gibt, sind so überfüllt, dass viele Kinder immer noch auf der Stra&#946;e leben.</font>\r\n<p class=\"type12txtbody\" align=\"justify\"><font size=\"3\">Im Jahre 2001 kamen Gil, Chantal und ihr Sohn Aaron nach\r\n<st1:country-region>\r\n<st1:place>Peru</st1:place></st1:country-region>. Gil kommt aus Belgien, Chantal ist Französin. In ihrem Gepäck befand sich viel Liebe und der Wunsch ihr Glück nach dem Beispiel von Mutter Theresa zu teilen…</font></p>\r\n<p class=\"type12txtbody\" align=\"left\"><font size=\"3\"></font>&nbsp;</p></td></tr></tbody></table><br />\r\n<table class=\"type12bgsilver\" cellspacing=\"0\" cellpadding=\"5\" width=\"100%\">\r\n<tbody>\r\n<tr>\r\n<td valign=\"top\" align=\"left\" width=\"50%\">\r\n<table class=\"type12bgwhite\" cellspacing=\"0\" cellpadding=\"0\" width=\"100%\">\r\n<tbody>\r\n<tr class=\"type12bggray\">\r\n<td valign=\"top\" align=\"left\"><img height=\"26\" alt=\"\" width=\"34\" src=\"/3rdparty/rvsitebuilder/themes/default/sitebuilder/images/layouttemplate/5b7a994edd971a207ddb115a9855ef99/tab01.gif\" /></td>\r\n<td class=\"type12txtbold\" width=\"99%\">\r\n<p class=\"type12txtbody\" align=\"left\"><font size=\"3\">Gil erzählt Euch die Geschichte der Gorriones …</font></p></td>\r\n<td valign=\"top\" align=\"right\"><img height=\"26\" alt=\"\" width=\"19\" src=\"/3rdparty/rvsitebuilder/themes/default/sitebuilder/images/layouttemplate/5b7a994edd971a207ddb115a9855ef99/tab02.gif\" /></td></tr>\r\n<tr>\r\n<td></td>\r\n<td valign=\"top\" align=\"left\">\r\n<div align=\"justify\">Hallo alle zusammen!</div>\r\n<div align=\"justify\">&nbsp;</div>\r\n<div align=\"justify\">Im Folgenden will ich versuchen Euch unsere kleine Geschichte zu erzählen und Euch zu berichten, wie und warum wir nach\r\n<st1:country-region>\r\n<st1:place>Peru</st1:place></st1:country-region> gekommen sind. Wir lebten in den Pyrenäen in Frankreich, meine Frau Chantal, mein Sohn Aaron und ich. Wir waren alle drei sehr zufrieden und wollten unser Glück mit denjenigen teilen, die weniger Glück in ihrem Leben haben. Wir lasen viele Bücher über Mutter Theresa, die so zu unserem Vorbild wurde. </div>\r\n<div align=\"justify\">&nbsp;</div>\r\n<div align=\"justify\">Wir hätten gerne ein Projekt in Frankreich entwickelt aber in unseren lieben\r\n<st1:city>\r\n<st1:place>Bergen</st1:place></st1:city> war das leider nicht möglich. Schade! Doch so machten wir uns nach Indien auf, um dort den Ärmsten der Ärmsten zu helfen. Und wer ist ärmer als Kinder, die\r\n<st1:city>\r\n<st1:place>leiden</st1:place></st1:city>? In Indien konnten wir jedoch nicht länger als 6 Monate im Jahr bleiben. Eine Lösung für dieses Problem wäre es gewesen für einen längeren Aufenthalt zu zahlen. Doch das ist Korruption und wir wollten unser Geld nicht auf diese Weise verwenden. Unser Geld sollte denjenigen zu Gute kommen, die es dringend benötigten und nicht denjenigen, die sowieso schon zu viel davon hatten. Wir fuhren also weiter nach\r\n<st1:country-region>\r\n<st1:place>Nepal</st1:place></st1:country-region>, wo wir auf das gleiche Problem trafen. Doch dort trafen wir auch eine Frau, die Präsidentin einer NGO in Frankreich war. Wir sind zu Mitgliedern dieser Organisation geworden und sahen auf Grund der Bemühungen dieser Frau, die an unser Projekt glaubte, neue Perspektiven am anderen Ende der Welt. </div>\r\n<div align=\"justify\">&nbsp;</div>\r\n<div align=\"justify\">So verließen wir\r\n<st1:city>\r\n<st1:place>Delhi</st1:place></st1:city> und kamen am 1. Juli 2001 in\r\n<st1:city>\r\n<st1:place>Lima</st1:place></st1:city> an. Circa 8 Monate später, am 5. März 2002, besaßen wir alle Papiere um die Türen unseres Hauses für die ersten Kinder zu öffnen. Stellen Sie sich unsere Freude vor, unser Traum wurde Realität!!!</div>\r\n<p align=\"justify\">Leider ist die Vereinspräsidentin schwer krank geworden und konnte unser Projekt nicht länger unterstützen. Wir befanden uns also allein in Peru, ohne Organisation, ohne Hilfe, allein mit dem Glauben, dass Gott uns nicht fallen lassen würde. Um das Projekt fortsetzen zu können, brauchten wir Geld und so verkauften wir unser komplettes Hab und Gut in Frankreich und investierten alles in das Casa Hogar Los Gorriones.</p>\r\n<p align=\"justify\">Doch Ende des Jahres kam der Moment, in dem der Sparstrumpf gänzlich ausgeschöpft war und so starteten wir einen Hilferuf. Jemand in Belgien hörte ihn und gründete bald eine Organisation. So konnten wir ab Januar 2003 mit unserem Projekt fortfahren, die monatlichen Ausgaben decken und die Gefahr die Türen unseres Hauses schlie&#946;en zu müssen, war gebannt. Seitdem boten mehrere Organisationen ihre Hilfe an (siehe Partner) und ermöglichten auf diese Weise die Vergrö&#946;erung unseres Hauses und die Entwicklung weiterer Projekte. Au&#946;erdem gibt es viele junge Leute, die helfen möchten und so kommen sie als Freiwillige nach Ayacucho und unterstützen unser Projekt.</p>\r\n<p align=\"justify\">Dennoch leben noch zu viele Kinder auf den Stra&#946;en Ayacuchos und wir haben oft keine andere Möglichkeit, als ihnen zu sagen, dass wir keinen Platz haben. Ein zweites Haus müsste eröffnet werden, um die Aufnahme mehrerer Kinder zu ermöglichen. Doch um dieses Haus zu bauen, brauchen wir Geld. Wir sind ständig auf der Suche nach Investoren und Spenden, doch wir sind sicher, dass wir es eines Tages schaffen werden!</p>\r\n<p align=\"justify\">Das ist also unsere kleine Geschichte. Für weitere aktuelle Informationen über die Gorriones: “La casa” und “Projekte”</p>\r\n<p align=\"justify\">Hasta pronto!</p>\r\n<p align=\"justify\">Gil und Chantal</p></td>\r\n<td></td></tr>\r\n<tr>\r\n<td valign=\"bottom\" align=\"left\"><img height=\"10\" alt=\"\" width=\"17\" src=\"/3rdparty/rvsitebuilder/themes/default/sitebuilder/images/layouttemplate/5b7a994edd971a207ddb115a9855ef99/corner03.gif\" /></td>\r\n<td width=\"99%\"></td>\r\n<td valign=\"bottom\" align=\"right\"><img height=\"10\" alt=\"\" width=\"9\" src=\"/3rdparty/rvsitebuilder/themes/default/sitebuilder/images/layouttemplate/5b7a994edd971a207ddb115a9855ef99/corner04.gif\" /></td></tr></tbody></table></td>\r\n<td valign=\"top\" align=\"left\" width=\"50%\">\r\n<table class=\"type12bgwhite\" cellspacing=\"0\" cellpadding=\"0\" width=\"100%\">\r\n<tbody>\r\n<tr class=\"type12bggray\">\r\n<td valign=\"top\" align=\"left\"><img height=\"26\" alt=\"\" width=\"34\" src=\"/3rdparty/rvsitebuilder/themes/default/sitebuilder/images/layouttemplate/5b7a994edd971a207ddb115a9855ef99/tab01.gif\" /></td>\r\n<td class=\"type12txtbold\" width=\"99%\"></td>\r\n<td valign=\"top\" align=\"right\"><img height=\"26\" alt=\"\" width=\"19\" src=\"/3rdparty/rvsitebuilder/themes/default/sitebuilder/images/layouttemplate/5b7a994edd971a207ddb115a9855ef99/tab02.gif\" /></td></tr>\r\n<tr>\r\n<td></td>\r\n<td valign=\"top\" align=\"left\"></td>\r\n<td></td></tr>\r\n<tr>\r\n<td valign=\"bottom\" align=\"left\"><img height=\"10\" alt=\"\" width=\"17\" src=\"/3rdparty/rvsitebuilder/themes/default/sitebuilder/images/layouttemplate/5b7a994edd971a207ddb115a9855ef99/corner03.gif\" /></td>\r\n<td width=\"99%\"></td>\r\n<td valign=\"bottom\" align=\"right\"><img height=\"10\" alt=\"\" width=\"9\" src=\"/3rdparty/rvsitebuilder/themes/default/sitebuilder/images/layouttemplate/5b7a994edd971a207ddb115a9855ef99/corner04.gif\" /></td></tr></tbody></table></td></tr></tbody></table></td>\r\n<td valign=\"top\" align=\"left\" width=\"30%\">\r\n<table cellspacing=\"0\" cellpadding=\"0\" width=\"100%\">\r\n<tbody>\r\n<tr>\r\n<td valign=\"top\" align=\"left\">\r\n<table cellspacing=\"0\" cellpadding=\"3\" width=\"100%\">\r\n<tbody>\r\n<tr>\r\n<td class=\"type12txtbold\" valign=\"top\" align=\"left\" colspan=\"2\">\r\n<div align=\"center\"><strong><font color=\"#bf4040\" size=\"3\">CHANTAL</font></strong></div>\r\n<div align=\"center\"><strong><font color=\"#bf4040\" size=\"3\">Wir bedauern es Ihnen keine gute Nachricht wissen zu&nbsp;lassen über Chantal.&nbsp;Sie&nbsp;war wieder im Krankenhaus untergebracht.&nbsp;Eine Bluttransfusion war notwendig, denn ihre Körper war nicht stark genug die Medizin zu vertragen gegen die Schmerzen. Ein jeder hofft auf ihrer Genesung, aber die Ärze avisierten den Abschied vor zu bereiten. Sie&nbsp;esst nicht mehr. </font></strong></div>\r\n<p align=\"center\"><strong><font color=\"#bf4040\" size=\"3\">Während ich diesen E-mail schreibe, bekomme ich die Nachricht das Chantal uns verlassen hat, jetzt Tonnersdag am 10 a.m. im Peru.!&nbsp; Armer Gil&nbsp;und Aaron, arme Kinder! Ich habe keine Wörter mehr. Mag Gott Ihnen stärken!</font></strong></p>\r\n<p align=\"center\"><strong><font color=\"#bf4040\" size=\"3\">Wir werden alles Mögliche tun Ihnen Beistand zu leisten.</font></strong></p></td></tr>\r\n<tr>\r\n<td class=\"type12rightpad01\" valign=\"top\"><img height=\"6\" alt=\"\" width=\"6\" src=\"/3rdparty/rvsitebuilder/themes/default/sitebuilder/images/layouttemplate/5b7a994edd971a207ddb115a9855ef99/bullet02.gif\" /></td>\r\n<td class=\"type12rightpad02\">\r\n<p align=\"center\"><img title=\"\" height=\"170\" alt=\"\" width=\"207\" src=\"/3rdparty/rvsitebuilder/project/871add3c9f1716ad9328e77a371e6050/images/x5.jpg\" /></p></td></tr>\r\n<tr>\r\n<td class=\"type12rightpad01\" valign=\"top\"><img height=\"6\" alt=\"\" width=\"6\" src=\"/3rdparty/rvsitebuilder/themes/default/sitebuilder/images/layouttemplate/5b7a994edd971a207ddb115a9855ef99/bullet02.gif\" /></td>\r\n<td class=\"type12rightpad02\"></td></tr>\r\n<tr>\r\n<td class=\"type12rightpad01\" valign=\"top\"><img height=\"6\" alt=\"\" width=\"6\" src=\"/3rdparty/rvsitebuilder/themes/default/sitebuilder/images/layouttemplate/5b7a994edd971a207ddb115a9855ef99/bullet02.gif\" /></td>\r\n<td class=\"type12rightpad02\"></td></tr>\r\n<tr>\r\n<td></td>\r\n<td class=\"type12linedash\"><img height=\"1\" alt=\"\" width=\"1\" src=\"/3rdparty/rvsitebuilder/themes/default/sitebuilder/images/layouttemplate/5b7a994edd971a207ddb115a9855ef99/spacer.gif\" /></td></tr>\r\n<tr>\r\n<td class=\"type12rightpad01\" valign=\"top\"><img height=\"6\" alt=\"\" width=\"6\" src=\"/3rdparty/rvsitebuilder/themes/default/sitebuilder/images/layouttemplate/5b7a994edd971a207ddb115a9855ef99/bullet02.gif\" /></td>\r\n<td class=\"type12rightpad02\"></td></tr>\r\n<tr>\r\n<td class=\"type12rightpad01\" valign=\"top\"><img height=\"6\" alt=\"\" width=\"6\" src=\"/3rdparty/rvsitebuilder/themes/default/sitebuilder/images/layouttemplate/5b7a994edd971a207ddb115a9855ef99/bullet02.gif\" /></td>\r\n<td class=\"type12rightpad02\"></td></tr>\r\n<tr>\r\n<td class=\"type12rightpad01\" valign=\"top\"><img height=\"6\" alt=\"\" width=\"6\" src=\"/3rdparty/rvsitebuilder/themes/default/sitebuilder/images/layouttemplate/5b7a994edd971a207ddb115a9855ef99/bullet02.gif\" /></td>\r\n<td class=\"type12rightpad02\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; </td></tr>\r\n<tr>\r\n<td></td>\r\n<td class=\"type12linedash\"><img height=\"1\" alt=\"\" width=\"1\" src=\"/3rdparty/rvsitebuilder/themes/default/sitebuilder/images/layouttemplate/5b7a994edd971a207ddb115a9855ef99/spacer.gif\" /></td></tr></tbody></table></td></tr></tbody></table></td></tr></tbody></table></td></tr></tbody></table>\r\n<div>&nbsp;</div>\r\n<div>&nbsp;</div>\r\n<div>&nbsp;</div>','','','','',null,null,'','','','',null,null,null,null);
REPLACE INTO rvs_project_page (project_page_id,rvs_usr_id,page_name,custom_page_name,rvs_component_id,page_data,head,page_title,meta_keyword,meta_description,effect_page_enter,duration_page_enter,meta_page_effect,rvs_link_id,target,form_template_id,i_pathway,i_mail,i_print,i_favorite) VALUES ('cd869d90b1b7bd4fad3ef75f347d0864','4a9479c2086afe0578d281c45072190b','La Casa','','0','<h2>Wer wir sind&nbsp;&nbsp;&nbsp; </h2>\r\n<p align=\"justify\"><font size=\"3\">Das Kinderheim Casa Hogar Los Gorriones wurde am 3. März 2002 von Gil und Chantal Van den Bergh gegründet. Es beherbergt momentan 25 vernachlässigte, verlassene und behinderte Kinder von 1-13 Jahren in der Stadt von Ayacucho. Das Casa Hogar Los Gorriones ist eine Einrichtung, die von dem peruanischen Staat registriert und anerkannt wurde und eng mit dem Familiengericht, den sozialen Diensten und der Behörde für Adoptionen zusammenarbeitet. In Koordination mit dem Jugendamt werden die Kinder auf professionelle Weise betreut und erzogen, medizinisch und psychologisch versorgt. Die Kinder sind Opfer von Gewalt und Alkoholismus in der Familie, waren Misshandlungen ausgesetzt und kamen mit einem hohen Grad an Verwahrlosung in das Casa Hogar Los Gorriones. Oft sind die Folgen der Unterernährung der Kinder so schwer, dass sie dauerhaft mit geistigen und physischen Behinderungen zu leben haben und ihr Zustand als irreversibel zu bezeichnen ist. Papa Gil, Mama Chantal und das Team der Gorriones legen sehr viel Wert auf ein familiäres Umfeld für die Kinder. Das Zusammenleben im Foyer basiert auf Respekt, Rücksichtnahme, Verantwortungsbewusstsein und Solidarität. </font></p>\r\n<h2 align=\"justify\">Wem wir helfen&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;</h2>\r\n<p align=\"justify\"><font size=\"3\">Das Casa nimmt Kinder von 0-18 Jahren auf, die ausgesetzt wurden oder von ihren Familien nicht mehr versorgt werden können und Hilfe brauchen. Viele von ihnen sind schwer geistig und körperlich behindert. Andere Einrichtungen in Ayacucho nehmen Kinder mit Behinderungen nicht auf, da diese eine spezielle Behandlung und Medikamente benötigen und in Folge dessen viel Zeit und Geld aufgewendet werden muss.</font></p>\r\n<p align=\"justify\"><font size=\"3\">Zahlreiche Eltern sind aufgrund ihrer Lebenssituation nicht in der Lage, ihre Kinder zu versorgen. Oft sehen sie die einzige Lösung darin, ihre Kinder in andere Hände zu geben. Nichtsdestotrotz kommt es für sie nicht in Frage, ihre Kinder zur Adoption frei zu geben. In diesen Fällen nehmen die Gorriones die Kinder zeitweise auf und arbeiten eng mit den Familien zusammen. Diese bleiben in Kontakt zu ihren Kindern und besuchen sie so oft sie möchten.</font></p>\r\n<p align=\"justify\"><font size=\"3\">In anderen Fällen sind die Eltern mit der Adoption ihrer Kinder einverstanden und die Vermittlung der Kinder familiengerichtlich anerkannt. Diese Kinder befinden sich auf einer nationalen Adoptionsliste.</font></p>\r\n<p align=\"justify\"><font size=\"3\">Viele Kinder werden traurigerweise auch - teilweise sehr jung und in schlechter Verfassung - einfach auf der Straße ausgesetzt.</font></p>\r\n<p align=\"justify\"><font size=\"3\">Die meisten “Spatzen” erreichen die Einrichtung in katastrophalen hygienischen Zuständen, sind unterernährt und leiden unter Krankheiten und psychischen Belastungen. Das sind die unglücklichen Folgen der Armut Perus.</font></p>\r\n<p><em><u>&nbsp;</u></em></p>\r\n<h2>Die Aufnahme der Kinder und der erfolgende Hilfeplan&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;</h2>\r\n<p align=\"justify\"><font size=\"3\">Die Aufnahme der Kinder hängt von keinem besonderen Kriterium ab. Das heißt, dass die Gorriones alle aufnehmen, die Hilfe brauchen, auch wenn die Behandlung der Kinder sehr kostspielig und zeitintensiv sein wird. Die Aufnahme erfolgt in Koordination mit dem Familiengericht und den Eltern oder Angehörigen der Kinder. Die Sozialarbeiterin untersucht, welche familiären Zustände vorliegen und wie stark die Verwahrlosung des Kindes vorangeschritten ist. Sobald das Kind in der Einrichtung aufgenommen wird, erfolgt die Erstellung eines Hilfeplans, der Erziehungsziele beinhaltet und auf das Alter und die mentale und körperliche Gesundheit des Kindes abgestimmt ist. Wenn das Kind eine besondere Behandlung benötigt, werden entsprechende Fachkräfte hinzugezogen, um dem Kind alle Chancen zu ermöglichen, sich eines normalen Lebens zu erfreuen. Die Entwicklung jedes Kindes wird von einer fortlaufenden Evaluation begleitet. Im Falle einer Freigabe der Kinder durch die Eltern, können die Gorriones eine Vermittlung des Kindes in die Wege leiten. Die Adoptionsanfragen werden gewissenhaft und den geltenden Gesetzen entsprechend bearbeitet. In Zusammenarbeit mit der Behörde für Adoptionen wird sichergestellt, dass sich der Prozess unter den besten Bedingungen vollzieht und das Kind sich in psychologischer, schulischer und sozialer Hinsicht ungestört weiterentwickeln kann. Infolge einer Adoption wird gleicherma&#946;en weiterverfolgt, wie es dem Kind in der neuen Familie geht. Alle notwendigen Schritte werden in den Fällen unternommen, in denen sich eine ungünstige Entwicklung für das Kind herausstellt.<em></em></font></p>\r\n<h2>Der familiäre Rückhalt&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;</h2>\r\n<p align=\"justify\"><font size=\"3\">Das Team der Gorriones ist davon überzeugt, dass es sehr wichtig ist, eine familiäre Atmosphäre zu schaffen und den Kindern auf diese Weise den Rückhalt einer Familie zu geben. Ein liebevoller Umgang, Zuwendung, Unterstützung und die Erziehung in einem familiären Rahmen haben somit äußerste Priorität. Wie in einer Familie wird ganz besonders auf die Heranbildung eines gesunden Selbstwertgefühls und auf die Persönlichkeitsgestaltung jedes einzelnen Kindes geachtet.</font></p>\r\n<h2>Gesundheitsversorgung&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;</h2>\r\n<p align=\"justify\"><font size=\"3\">Jedes Kind wird von einem Arzt versorgt und bekommt die medizinische Hilfe, die es benötigt, sei es physischer oder psychologischer Art. Mehrere Kinder des Hauses haben eine schwere körperliche Behinderung und werden täglich mit entsprechenden körperlichen Übungen stimuliert. Eine gesunde Ernährung ist für die gesunde Entwicklung der Kinder entscheidend, besonders weil fast alle zuvor unter Unterernährung litten. Im Casa Hogar wird von einer Köchin dreimal täglich ein gesundes Essen für die ganze Familie zubereitet.</font></p>\r\n<h2>Bildung&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;</h2>\r\n<p align=\"justify\"><font size=\"3\">Mithilfe der vernetzten Organisationen und der individuellen Spenden kann jedem Kind, das sich im Schulalter befindet und sich fortbewegen kann, eine adäquate Schulbildung in einer privaten Schule ermöglicht werden. Für die körperlich oder geistig eingeschränkten Kinder, die aufgrund ihrer Behinderung keine Schule besuchen können, werden erzieherische Maßnahmen und eine schulische Betreuung im Casa gewährleistet. Dies erfolgt durch Freiwillige oder durch das hauseigene ausgebildete Team, das unter anderem aus Erziehern, Krankenpflegern, Sozialarbeitern und einer Psychologin besteht.</font></p>\r\n<h3>Allgemeine Situations- und Problembeschreibung der Kinder und Erwachsenen in Ayacucho&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;</h3>\r\n<p><font size=\"3\">Wie in vielen anderen Städten Lateinamerikas sind die Armut und die Arbeitslosigkeit die schwerwiegendsten Probleme in Ayacucho. Wichtig zu wissen ist jedoch, dass sich in Ayacucho in den 70er Jahren die terroristische Bewegung des leuchtenden Pfads (Sendero Luminoso) formierte. Der Bürgerkrieg, der von 1980 bis 1992 zwischen der Terror&shy;gruppe und den nationalen Sicher&shy;heitskräften tobte, forderte über 70000 Tote. 85 Prozent aller Ermordeten und Ver&shy;schwundenen stammten aus den Regionen Huancavelica, Apurimac, San Martin und Ayacucho. Noch heute hat Ayacucho unter den Folgen des Kampfes zu leiden, da tausende Menschen in der Kriegszeit aus Sicherheitsgründen ihre Häuser und Dörfer in den Bergen um Ayacucho verließen um in der Stadt Schutz vor der terroristischen Bewegung und gleichzeitig Arbeit zu finden. Diese Prozesse haben ein starkes Ungleichgewicht geschaffen. Denn in der Stadt angekommen, waren die ehemaligen Bauern in Folge der zunehmenden Bevölkerungsdichte und Armut von einer extremen Arbeitslosigkeit betroffen. Rund 80 Prozent der erwerbsfähigen Peruanerinnen und Perua&shy;ner sind geringfügig entlohnt oder haben gar keine Arbeit. Ein Fünftel lebt in extremer Armut. Die Lebensunterhaltungskosten sind gestiegen und die Menschen sind so arm, dass sie trotz aller Bemühungen die eigenen Familien nicht ernähren können. </font></p>\r\n<p><font size=\"3\">Oft werden die Kinder von ihren Eltern auf die Straße geschickt um zu arbeiten und mit dem wenigen, das sie verdienen, müssen sie meist die ganze Familie ernähren. Sie sind ambulante Verkäufer von Süßigkeiten, betteln, singen und bieten kleine Dienstleistungen wie Schuhe putzen an. Aufgrund dieser Überlebenssicherung ist es ihnen meist unmöglich zur Schule zu gehen.</font></p>\r\n<p><font size=\"3\">Ihre Eltern waren Kinder zur Zeit des Terrorismus und sind ohne Schule und Erziehung aufgewachsen. Sie sind oft Analphabeten, mussten selbst früh anfangen zu arbeiten und haben nicht mehr kennen gelernt als Hunger und Gewalt. Diese Folgen des Krieges und die traumaverursachenden Gewalterfahrungen wirken sich wiederum in hohem Maße auf ihre Kinder aus.</font></p>\r\n<p><font size=\"3\">Auch der Alkohol ist ein großes Problem. Es sind vor allem die Männer, die ihn trinken, meist den 96-prozentigen mit Wasser verdünnt. Größtenteils handelt es sich dabei zudem um den nicht für den Konsum geeigneten Methylalkohol, der den Sehnerv angreift, sehr gesundheitsschädlich ist und aggressiv macht. Die Männer trinken um ihre Probleme zu vergessen, werden aber extrem aggressiv und misshandeln ihre Kinder. Viele der Kinder flüchten vor ihren Vätern auf die Straße oder laufen weg, weil zu Hause zwischen den Eltern nur Streit und Gewalt herrscht.</font></p>\r\n<p><font size=\"3\">Eine andere Konsequenz aus der langen Zeit des Terrorismus ist es, dass es siebenmal mehr Frauen als Männer in der Region gibt, da der Terrorismus einer unglaublich hohen Zahl an Männern und Jugendlichen den Tod gebracht hat. Die Folge ist, dass die Männer diesen Frauenüberschuss zu ihrem Vorteil ausnutzen, indem sie zum Beispiel ihre Frauen betrügen, sich scheiden lassen oder die kinderreichen Familien verlassen, um sich andere Frauen zu suchen. Die Kinder wachsen oft ohne die Präsenz ihres Vaters auf und müssen schon früh die entsprechenden Versorgerrollen übernehmen. </font></p>\r\n<p><font size=\"3\">Unter diesen Bedingungen leiden besonders die Kinder. In Folge der Armut und Arbeitslosigkeit werden viele von ihnen ausgesetzt und versuchen auf der Straße zu überleben. Viele Eltern sehen in ihrer Verzweiflung eine Chance für das gesicherte Überleben ihrer Kinder darin, sie in andere Hände zu geben. </font></p>\r\n<p><font size=\"3\">Bei Kindern mit Behinderungen ergibt sich eine besondere Problematik. Für die Erklärung der Behinderungen werden häufig religiöse Anschauungen herangezogen. Die Eltern schämen sich für ihre behinderten Kinder, beurteilen die Behinderung als Strafe Gottes oder als Schande. Aus diesem Grund möchten sie ihre Kinder nicht in der Öffentlichkeit zeigen. Andere Eltern setzen ihre behinderten Kinder aus, weil ihnen die finanziellen Mittel für Fördermöglichkeiten und die Kenntnisse für einen angemessenen Umgang mit behinderten Kindern fehlen. </font></p>\r\n<p><font size=\"3\">Die ausgesetzten und auf der Straße lebenden Kinder sind oft schwer unterernährt und krank und haben traumatische Erfahrungen machen müssen. Die Stadtverwaltung verschließt die Augen vor den Problemen und hilft den armen Familien nicht. Auffanglager und Kinderheime sind sehr selten und die Kapazitäten daher sehr begrenzt. Die Kinderheime, die es gibt, sind so überfüllt, dass viele Kinder abgelehnt werden müssen und deshalb immer noch auf der Stra&#946;e leben. Auf der Straße sind sie vielen Gefahren ausgesetzt. Nicht nur Gewalt, Drogenkonsum und organisierte Kriminalität erwarten sie, auch von Kindesmissbrauch und Kinderprostitution ist in diesem Zusammenhang zu hören. Es ist demzufolge nicht verwunderlich, dass unter diesen Bedingungen die Jugendkriminalität ein weiteres Problem darstellt. In der Öffentlichkeit werden diese sozialen Probleme aber auch verdrängt und auf der Straße lebende Jugendliche schnell marginalisiert und kriminalisiert. Die Reintegration von Straßenkindern in die Gesellschaft ist oft schwierig, vor allem wenn sie sich in einem aggressiven Umfeld befinden, wo das Recht des Stärkeren regiert. </font></p>\r\n<p><font size=\"3\">Des Weiteren haben viele Jugendliche früh sexuelle Beziehungen, wodurch es viele jugendliche Eltern gibt. Meistens wollen auch die jungen Väter, dem Beispiel ihrer Väter folgend, keine Verantwortung übernehmen und lassen die jungen Mütter allein.</font></p>\r\n<p><font size=\"3\">Zusammenfassend lässt sich sagen, dass Kinder, die so früh mit den Härten des Lebens konfrontiert werden, unter Traumata leiden, die sich wiederum auf ihre eigenen Kinder niederschlagen. Traumatische Erfahrungen werden an die nächste Generation weiter gegeben, wenn diese Kreisläufe nicht unterbrochen werden. Um diese Zirkularität zu durchbrechen sind Präventivmaßnahmen, die den Kindern ein liebevolles Zuhause, Unterstützung und Rückhalt bieten, unabdingbar. </font></p>\r\n<div><font size=\"3\">&nbsp;</font></div>\r\n<div>&nbsp;</div>\r\n<div>&nbsp;</div>\r\n<div>&nbsp;</div>','','','','',null,null,'','','','',null,null,null,null);
REPLACE INTO rvs_project_page (project_page_id,rvs_usr_id,page_name,custom_page_name,rvs_component_id,page_data,head,page_title,meta_keyword,meta_description,effect_page_enter,duration_page_enter,meta_page_effect,rvs_link_id,target,form_template_id,i_pathway,i_mail,i_print,i_favorite) VALUES ('b3eae616f66a7baa8f7fd29f7396ffbc','4a9479c2086afe0578d281c45072190b','Helfen und Spenden','','0','<h2>Spenden&nbsp;&nbsp;</h2>\r\n<p align=\"justify\"><font size=\"3\">Die Existenzsicherung unserer Einrichtung hängt hauptsächlich von privaten Spenden und Programmen kleiner Organisationen ab. Ohne Ihre großzügige Hilfe wäre unsere Arbeit unmöglich! Bitte helfen Sie uns zu helfen! Ob es nun Spenden für die Schulbildung eines Kindes, für allgemeine Aktivitäten des Hauses oder Beiträge für ein bestimmtes, Sie ansprechendes Projekt sind, ihre Spenden sind sehr wichtig für uns! </font></p>\r\n<div align=\"justify\"><font size=\"3\">Aufgrund der hohen Überweisungskosten lohnt es sich per Kreditkarte oder mithilfe des Paypal-Systems zu spenden. Paypal ist ein sehr sicherer und verlässlicher Online-Zahlungsservice, für den kein Bankkonto benötigt wird und Sie sich einmalig und kostenlos ein Paypal-Konto einrichten. Ihre Spenden kommen so direkt auf unserem Konto an, ohne dass Kosten für den Geldtransfer entstehen. Bitte klicken Sie hier:</font></div>\r\n<div align=\"justify\">\r\n<form method=\"post\" action=\"https://www.paypal.com/cgi-bin/webscr\"><input type=\"hidden\" value=\"_donations\" name=\"cmd\" /><input type=\"hidden\" value=\"info@casahogarlosgorriones.org\" name=\"business\" /><input type=\"hidden\" value=\"Spenden\" name=\"item_name\" /><input type=\"hidden\" value=\"2\" name=\"no_shipping\" /><input type=\"hidden\" value=\"http://www.casahogarlosgorriones.org\" name=\"return\" /><input type=\"hidden\" value=\"CAD\" name=\"currency_code\" /><input type=\"hidden\" value=\"0\" name=\"tax\" /><input type=\"hidden\" value=\"CA\" name=\"lc\" /><input type=\"hidden\" value=\"PP-DonationsBF\" name=\"bn\" /><input type=\"image\" alt=\"PayPal - The safer, easier way to pay online!\" border=\"0\" name=\"submit\" src=\"https://www.paypal.com/en_US/i/btn/btn_donateCC_LG.gif\" /><img height=\"1\" alt=\"\" width=\"1\" border=\"0\" src=\"https://www.paypal.com/en_US/i/scr/pixel.gif\" /></form></div>\r\n<p>&nbsp;</p>\r\n<h2>Mitglied werden - gemeinsam etwas erreichen!!!&nbsp;</h2>\r\n<p align=\"justify\"><font size=\"3\">Mitglied zu werden ist eine Möglichkeit zum Freundeskreis der Gorriones zu gehören, die Aktivitäten der Einrichtung zu unterstützen und regelmäßig Informationen über die Kinder und unsere Tätigkeiten zu bekommen. Als Mitglied empfangen Sie - zusätzlich zum Jahresbericht - alle 2 Monate einen Infobrief mit den Neuigkeiten der Einrichtung. Sie haben die Möglichkeit uns Ihre Kommentare und Ideen zum Geschehen im Foyer mitzuteilen und einen Austausch über die Projekte des Kinderheims anzuregen. Mitglied sein trägt vor allem dazu bei, alle Freunde der Gorriones zu versammeln, um das Projekt wirksamer zu unterstützen. Sobald Sie sich einschreiben, engagieren Sie sich mit 20 $ US monatlich für eine Zeit von 12 Monaten. Um Mitglied zu werden, klicken Sie hier:&nbsp;&nbsp;</font> </p>\r\n<p>\r\n<form method=\"post\" action=\"https://www.paypal.com/cgi-bin/webscr\"><input type=\"hidden\" value=\"_s-xclick\" name=\"cmd\" /><input type=\"image\" alt=\"PayPal - The safer, easier way to pay online!\" border=\"0\" name=\"submit\" src=\"https://www.paypal.com/en_US/i/btn/btn_subscribeCC_LG.gif\" /><img height=\"1\" alt=\"\" width=\"1\" border=\"0\" src=\"https://www.paypal.com/en_US/i/scr/pixel.gif\" /><input type=\"hidden\" value=\"-----BEGIN PKCS7----------END PKCS7-----&#13;&#10;\" name=\"encrypted\" /></form></p>\r\n<p align=\"justify\"><font size=\"3\">Leider können die einzelnen Spenden von Privatpersonen und die Sammlungen von Spendengeldern durch befreundete Organisationen unsere immensen Kosten schon lange nicht mehr decken. Insbesondere die hohen Personalkosten bereiten uns jeden Monat Kopfzerbrechen.</font></p>\r\n<p align=\"justify\"><font size=\"3\">Seit Gil und Chantal ihr gesamtes Hab und Gut in Frankreich verkauften, alle Ersparnisse ausgeschöpft waren und die finanzielle Unterstützung einer anderen Organisation beendet wurde, sind wir tagtäglich via Internet auf der Suche nach neuen Geldern. Ein weiteres Standbein der Finanzierung soll durch ein Patenschaftssystem geschaffen werden.</font></p>\r\n<p>&nbsp;</p>\r\n<h3>Aktivitäten in Ihrer oder anderen Einrichtungen organisieren&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;</h3>\r\n<p align=\"justify\"><font size=\"3\">Falls Sie lieber Ihre Zeit und Energie investieren möchten, sind Sie auch dazu herzlich eingeladen. Möchten Sie beispielsweise ein Essen oder ein Barbecue veranstalten oder andere Aktivitäten initiieren um Geld für unsere Kinder zu sammeln? Sind Sie als Lehrkraft tätig und möchten mit Ihren Schülern ein Projekt zur Unterstützung der Gorriones veranstalten? Teilen Sie uns ihre Ideen mit! Wir helfen Ihnen gerne diese zu verwirklichen und Benefizveranstaltungen zu organisieren. Kontaktieren Sie zu diesem Zweck bitte die nächstliegendes Partnerorganisation.</font></p>\r\n<p><u>&nbsp;</u></p>\r\n<h3>Die Webseite finanzieren&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;</h3>\r\n<p align=\"justify\"><font size=\"3\">Diese Webseite ist für uns ein wertvolles und sehr nützliches Werkzeug um Informationen über unser Projekt zu vermitteln. Sie fördert unsere Arbeit und erreicht in 4 Sprachen viele unserer Freunde auf dem ganzen Planeten. Die Webseite wird derzeit kostenlos von einer Freundin verwaltet und von Freiwilligen übersetzt. Dennoch bleiben die jährlichen Kosten von 300 $ US für das Web-Hosting. </font></p>\r\n<p align=\"justify\"><font size=\"3\">Wenn Sie die Gorriones unterstützen wollen oder weitere Fragen haben, kontaktieren Sie uns bitte unter folgender Adresse:</font></p>\r\n<p align=\"justify\"><a href=\"&#109;&#97;&#105;&#108;&#116;&#111;&#58;&#73;&#78;&#70;&#79;&#64;&#67;&#65;&#83;&#65;&#72;&#79;&#71;&#65;&#82;&#76;&#79;&#83;&#71;&#79;&#82;&#82;&#73;&#79;&#78;&#69;&#83;&#46;&#79;&#82;&#71;\"><font size=\"3\">&#73;&#78;&#70;&#79;&#64;&#67;&#65;&#83;&#65;&#72;&#79;&#71;&#65;&#82;&#76;&#79;&#83;&#71;&#79;&#82;&#82;&#73;&#79;&#78;&#69;&#83;&#46;&#79;&#82;&#71;</font></a></p>\r\n<h2>Freiwilligenarbeit im Casa Hogar Los Gorriones&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; </h2>\r\n<p align=\"justify\"><font size=\"3\">Freiwillige, die gerne eine unvergessliche Erfahrung machen möchten und sich vorstellen können direkt vor Ort im Casa Hogar mit den Kindern zusammenzuarbeiten, sind herzlich eingeladen sich direkt mit dem Freiwilligenkoordinator in Verbindung zu setzen. Für Dezember 2008 brauchen wir dringend dynamische Freiwillige!</font></p>\r\n<p align=\"justify\"><a href=\"&#109;&#97;&#105;&#108;&#116;&#111;&#58;&#105;&#99;&#104;&#97;&#51;&#56;&#64;&#104;&#111;&#116;&#109;&#97;&#105;&#108;&#46;&#102;&#114;\"><font size=\"3\">&#106;&#111;&#114;&#100;&#97;&#110;&#46;&#103;&#111;&#114;&#114;&#105;&#111;&#110;&#101;&#115;&#64;&#104;&#111;&#116;&#109;&#97;&#105;&#108;&#46;&#102;&#114;</font></a></p>\r\n<div>&nbsp;</div>\r\n<div>&nbsp;</div>\r\n<div>&nbsp;</div>\r\n<div>&nbsp;</div>','','','','',null,null,'','','','',null,null,null,null);
REPLACE INTO rvs_project_page (project_page_id,rvs_usr_id,page_name,custom_page_name,rvs_component_id,page_data,head,page_title,meta_keyword,meta_description,effect_page_enter,duration_page_enter,meta_page_effect,rvs_link_id,target,form_template_id,i_pathway,i_mail,i_print,i_favorite) VALUES ('1906443f4255098f6b2bc96e587cc6ae','4a9479c2086afe0578d281c45072190b','Kontakt','','0','','','','','',null,null,'','','','',null,null,null,null);
REPLACE INTO rvs_project_page (project_page_id,rvs_usr_id,page_name,custom_page_name,rvs_component_id,page_data,head,page_title,meta_keyword,meta_description,effect_page_enter,duration_page_enter,meta_page_effect,rvs_link_id,target,form_template_id,i_pathway,i_mail,i_print,i_favorite) VALUES ('da5ff0a9eef2030398e67d68a0752cc7','4a9479c2086afe0578d281c45072190b','Français','','0','','','','','',null,null,'','70fc75dc813cf559e852438517c956d3','_self','',null,null,null,null);
REPLACE INTO rvs_project_page (project_page_id,rvs_usr_id,page_name,custom_page_name,rvs_component_id,page_data,head,page_title,meta_keyword,meta_description,effect_page_enter,duration_page_enter,meta_page_effect,rvs_link_id,target,form_template_id,i_pathway,i_mail,i_print,i_favorite) VALUES ('1540e0f9a419d02213eb2638c6b338cd','4a9479c2086afe0578d281c45072190b','Geschichte der kinder','','0','<p><br />\r\n<!-- ####### LAYOUT CSS: MODIFY COLOR, FONT AND BOX SIZE HERE BUT DO NOT DELETE. ####### -->\r\n<style type=\"text/css\">\r\n.ttype06head{\r\n	font-size:120%;\r\n        color:#000000;\r\n	font-weight:bold;\r\n	text-align:left;\r\n	padding:3px 3px;\r\n	border:#949492 solid 1px;\r\n	border-left:#949492 solid 10px;\r\n	background-color:#CCCCCC;\r\n}\r\n.ttype06subhead{\r\n	text-align:left;\r\n	padding:3px 3px;\r\n}</style><!-- ########################### END CSS LAYOUT ###########################  -->\r\n<table cellspacing=\"0\" cellpadding=\"0\" width=\"100%\">\r\n<tbody>\r\n<tr>\r\n<td class=\"ttype06head\" valign=\"top\" align=\"left\"><font size=\"5\">Die Geschichte der Kinder</font></td></tr>\r\n<tr>\r\n<td class=\"ttype06subhead\" valign=\"top\" align=\"left\" bgcolor=\"#ffffff\">\r\n<p align=\"justify\"><font size=\"3\"><strong>Ruth-Karina</strong> ist eins der ersten drei Babys gewesen, die am Tag der Heimeröffnung zu uns gebracht wurden. Sie wurde zwischen Müllsäcken neben Schweinen, Ratten und streunenden Hunden gefunden und daraufhin ins Krankenhaus gebracht. </font></p>\r\n<p align=\"justify\"><font size=\"3\">Die Folgen der Unterernährung sind so schwer, daß die Ärzte eine zerebrale Lähmung (Gehirnlähmung) diagnostiziert haben. Sie konnte weder ihre linke noch ihre rechte Körperhälfte bewegen. Die Bewegungen ihrer Arme und Beine erfolgen unkontrolliert. Ihr rechtes Bein ist zum Beispiel dauernd in Bewegung. Ihr ganzer Körper ist mit Brandnarben bedeckt, der kleinste Lärm läßt sie zusammenzucken und sie verkrampft sich. Des Weiteren hatte sie viele Blutergüsse, was ebenfalls darauf schlie&#946;en lässt, dass sie Opfer von Gewalt in der Familie geworden ist. Ruth-Karina ist blind, kann nicht sprechen und wog gerade 6 Kilogramm als sie ankam. Zu diesem Zeitpunkt war sie schwer krank. Während der ersten 8 Monate hat sie Tag und Nacht geweint. Sie war das traurigste Kind, das wir jemals in unserem Leben gesehen haben. Wir haben sie mehrmals in einem Krankenhaus in <?xml:namespace prefix = st1>\r\n<st1:city>\r\n<st1:place>Lima</st1:place></st1:city> untersuchen lassen. Ihr Körper wies weitere Symptome von Sauerstoffmangel auf, die vermutlich mit Problemen bei der Geburt oder späteren Gewalteinwirkungen zusammenhängen. Im August 2002 wurde sie wegen einiger Blutgerinnsel im Gehirn operiert, die wahrscheinlich ebenfalls Folge von Gewalteinwirkung waren. Man kann sich nicht vorstellen, was dieses Kind in seinem kurzen Leben schon erleiden mußte. Man erzählt sich, dass Ruth-Karina bei ihrer Geburt gesund war und ihre Mutter psychisch krank. Sie soll ihr Kind einer Frau gegeben haben, die an ihrem Haus vorbei kam. Ruth-Karina wird es nie erzählen können, sie ist zu einem Kind ohne Vergangenheit geworden. Nach der Operation hat Ruth-Karina angefangen sich wie ein 6-Monate altes Baby verständlich zu machen, nur mit einigen Lauten. Doch das grö&#946;te Wunder war, sie begann zu lachen. Nach und nach hat sie uns ihr Vertrauen geschenkt. Sie hat immer noch gro&#946;e Verdauungsschwierigkeiten und kann nur flüssige Nahrung zu sich nehmen. Die Mitarbeiter brauchen sehr viel Geduld um ihr zu\r\n<st1:city>\r\n<st1:place>essen</st1:place></st1:city> und zu trinken zu geben. Zwei mal am Tag machen wir Übungen zur Rehabilitierung und Stimulation mit ihr, soviel geht. Laut den Ärzten ist ihr Zustand irreversibel aber wir hören nicht auf an ein Wunder zu glauben, das Wunder der Liebe. Seit dem 31. Dezember 2003 hei&#946;t sie <strong>Ruth-Karina Van den Bergh</strong>!!! Sie ist nun unsere Tochter, wir haben sie offiziel adoptiert!</font>\r\n<p align=\"justify\"><font size=\"3\"><strong>Yaneth und José-Luis</strong> sind Zwillinge. Sie waren 13 Monate alt als sie zu uns kamen. Ihre Mutter ist bei der Geburt gestorben, ihr Vater verschwunden. Als sie ankamen, wogen sie 4 und 6 Kilogramm. Ihre Großeltern kümmerten sich um sie, waren jedoch so arm, daß sie die Kinder nicht ernähren konnten. José Luis litt an schwerem Asthma und mußte zweimal am Tag ins Krankenhaus gebracht warden um sich behandeln zu lassen. Er war sehr anfällig für Krankheiten und hatte regelmäßig hohes Fieber. Oft mußten wir ihn schleunigst ins Krankenhaus bringen. Die Zwillinge sind im Vergleich zu anderen Kindern in ihrem Alter in vielen Bereichen zurückgeblieben. Yaneth ist ein sehr gut erzogenes Mädchen mit strahlenden Augen. José fing nach einigen Wochen an zu gehen und seine Schwester folgte ihm kurz darauf. Die beiden sind trotzdem oft krank und brauchen viele Medikamente. Dank der Fürsorge unseres Personals, das sich um sie sorgt, haben die Beiden schon große Fortschritte gemacht.</font></p>\r\n<p align=\"justify\"><font size=\"3\"><strong>Natalie</strong> war 18 Monate alt und wog 6,3 Kilogramm als sie zu uns kam. Sie wurde von ihrer Mutter, die erst 14 Jahre alt war, ausgesetzt. Ihr Vater ist unbekannt. Natalie war wie viele andere Kinder unterernährt und wenig resistent gegen Krankheiten. Sie braucht mehr Stimulation als die anderen. Der Arzt meint, sie habe einen Herzfehler. Sie braucht einen speziellen Ernährungsplan, da sie viele Nahrungsmittel nicht verträgt. Sie bekommt regelmäßig Vitamine, Früchte und frische Milch. Es scheint als habe sie die Trennung von ihrer Mutter noch nicht überwunden. Die Familiensituation ist uns nicht ganz klar. Ihre Mutter lebte mit einem Mann zusammen, der das Kind nicht wollte. Natalie kann stundenlang nur daliegen ohne sich zu bewegen oder ein Geräusch zu machen. Sie ißt langsam und sehr wenig. Obwohl wir sie schon 10 Monate intensiv pflegen, wiegt sie nur 10 Kilogramm. Natalie fängt langsam an zu gehen, mit nun ca. zwei Jahren. </font></p>\r\n<p align=\"justify\"><font size=\"3\">Diese vier Kinder waren die ersten, die ankamen als wir das Haus am 5. März 2002 öffneten.</font></p>\r\n<p align=\"justify\"><font size=\"3\">Später kamen vier Kinder der gleichen Familie: <strong>Haydée</strong> mit 14,<strong> Eduardo</strong> mit 12, <strong>Santha</strong> mit 8 und <strong>Jonathan</strong> mit 5 Jahren. Der Vater ist Alkoholiker und gewalttätig. Er hat das Haus verlassen. Die Mutter ist wegen Drogenhandel im Gefängnis. Die arme Frau ist wahrscheinlich unschuldig: Man hat ihr ein Paket in die Tasche gesteckt. In Perú wird die präventive Haft angewendet. Sie dauert ein Jahr. Danach besteht die Gefahr, dass sie um zehn weitere Jahre verlängert wird, wenn die Mutter der vier Kinder schuldig gesprochen wird. Alle vier Kinder waren voller Parasiten (bis zu\r\n<st1:metricconverter productid=\"30 cm\">30 cm</st1:metricconverter> lang), hatten Blutprobleme und Hautkrankheiten. Sie brauchten viel Pflege und viele sehr teure Medikamente. Die Bettwäsche und die Leintücher mußten jeden Tag gewaschen werden. Bezüglich ihrer Schulbildung sind sie stark im Rückstand. Eduardo besucht mit seinen 12 Jahren zum fünften Mal die zweite Klasse, in der sonst siebenjährige Kinder sitzen. Haydée ist mit 14 Jahren in der vierten Klasse, Santha in der ersten. Seit August 2002 wohnen sie bei ihrer Tante, die sich bereit erklärt hat für die Kinder zu sorgen. Die Reintegration in die Familien der Kinder ist eines unserer Ziele. Sie werden weiterhin vom Jugendamt betreut.</font></p>\r\n<p align=\"justify\"><font size=\"3\">Am ersten Mai 2002 kam <strong>Elyzabeth</strong> (sie will, dass wir Fiorela zu ihr sagen). Fiorela lebte auf der Straße. Sie wurde von ihrer Mutter verlassen und von ihren Brüdern mißhandelt. Sie ist von zu Hause weggelaufen und lebte auf der Straße, wo sie viel über das Leben als Straßenkind lernte. Schon von zu Hause brachte sie viele Erfahrungen mit. Ihre Mutter verkaufte pornographische Straßenzeitungen. Obwohl sie erst 6 Jahre alt ist, wurde sie schon von vielen Heimen hinausgeworfen. Aufgrund ihres Benehmens stellte sie kein gutes Beispiel für andere Kinder dar. Sie kannte viele unanständige Wörter und verwendete diese dauernd. Wir haben unsere Türen auch für sie geöffnet. Am Anfang log sie sehr oft, um nicht zu sagen immer. Ihr fiel das Zusammenleben mit den anderen Kindern sehr schwer, sie war sehr aggressiv. Nach zwei Wochen hat sie sich gut eingelebt und scheint sich bei uns wohlzufühlen. Sie betrachtet Gil und Chantal als ihre Eltern und hat ihren Platz unter den anderen Kindern gefunden, von denen sie auch akzeptiert wird. Sie braucht viel Liebe und das ist genau das, was sie bekommt. Sie ist den ganzen Tag zufrieden, singt und ist ein kleiner Sonnenschein im Haus. Mit Liebe kann man Berge versetzen!</font></p>\r\n<p align=\"justify\"><font size=\"3\">Nach <strong>Fiorela</strong> kamen J<strong>esús</strong> (14) und <strong>Jorge</strong> (7) für einen Monat zu uns mit der Hoffnung, dass das Jugendamt einen Platz für sie findet. Es sind Kinder mit vielen Problemen. Sie stehlen, lügen und haben keinen Respekt. All das sind Konsequenzen ihrer Erziehung. Leider konnten wir während diesen Monats keine großen Veränderungen bewirken.</font></p>\r\n<p align=\"justify\"><font size=\"3\">Am 21. August 2002 kamen 3 weitere Kinder: <strong>Juan Carlos</strong> (11 Jahre), <strong>Elmer</strong> (9 Jahre) und<strong> Emerson</strong> (5 Jahre). Ihre Mutter starb im Mai zuvor. Zwei Monate später wurde ihr Vater mit Kokain festgenommen und sitzt nun wegen Drogenhandels im Gefängnis. Es sind liebe Kinder, deren Leben sich innerhalb weniger Monate schlagartig verändert hat. Sie kommen aus dem Regenwald, wo die Schulbildung sehr schlecht ist. Die meiste Zeit müssen die Kinder ihren Eltern beim Arbeiten helfen. Deshalb waren bei ihnen einige Lernrückstände festzustellen. <strong>Juan Carlos</strong> kann gerade einmal lesen, versteht jedoch nicht, was er liest. Sie haben sich hier gut zurechtgefunden und scheinen glücklich zu sein. Dafür war sehr viel Verantwortung und elterliche Autorität nötig. Die Kinder fanden in Gil und Chantal Menschen, die ihnen zuhören, sie verpflegen, erziehen und lieben. Sie besuchen regelmä&#946;ig ihren Vater im Gefängnis. Es ist sehr wichtig für sie den Kontakt zu ihm zu bewahren.</font></p>\r\n<p align=\"justify\"><font size=\"3\">Heute sind <strong>Juan Carlos</strong> und <strong>Elmer</strong> in Urpi, einem staatlichen Kinderheim für Jungen von 6-18 Jahren. Angesichts ihres Alters und des geringen Platzes in unserem Haus (Jungen und Mädchen müssen sich ihre Zimmer teilen, da wir unseren gro&#946;en Traum ein zweites Haus zu bauen, noch nicht realisieren konnten) haben wir beschlossen, die älteren Kinder vorläufig zu trennen. <strong>Emerson </strong>hat es bevorzugt hier bei uns zu bleiben.</font></p>\r\n<p align=\"justify\"><font size=\"3\">Am 27. August 2002 brachte uns das Jugendamt 2 Kinder: <strong>Edu</strong> (4 Jahre) und seinen Bruder <strong>Luís</strong> (2 Jahre). Sie wurden bei den Straßenkindern gefunden. Wir wußten nicht, woher sie kamen, wie sie hießen, ob sie ausgesetzt wurden oder vermißt werden. Einige Tage später kam ihre Mutter mit einer eigenartigen Geschichte. Sie suchte ihre Kinder und wollte sie zurückhaben. Das Jugendamt will sicher gehen, dass sie gut mit ihren Kindern umgeht und verfügte deshalb, dass die Kinder bis zur Überprüfung bei uns bleiben. </font></p>\r\n<p align=\"justify\"><font size=\"3\">Das ist in wenigen Worten das Ergebnis von 6 Monaten Kinderheim „Los Gorriones&quot; gewesen. Es kamen noch viele weitere Kinder, deren Geschichten hier nicht <em>alle</em> erzählt werden können. Dennoch ein kurzer Überblick:</font></p>\r\n<p align=\"justify\"><font size=\"3\">Unter den 25 Kindern, die heute, im Oktober 2007, mit uns im Casa Hogar leben, gibt es viele Geschwister. </font></p>\r\n<p align=\"justify\"><font size=\"3\">Da sind z.B. der 3-jährige Fredy und seine 5 Jahre alte Schwester Deisse, die vor einem Jahr zu uns kamen, weil ihre Mutter starb und ihr Vater sich nicht um sie kümmern kann. </font></p>\r\n<p align=\"justify\"><font size=\"3\">Da sind die 5 Kinder der <strong>Familie Lopez: Favio</strong> (11),<strong> Luis</strong> (9),<strong> Abel</strong> (7), <strong>Brenda</strong> (5) und <strong>Sergio</strong> (3), die uns in einer Nacht im März 2004 von der Polizei geschickt wurden, weil ihre Mutter verschwand und sie mehrere Tage alleine zu Hause gelassen hatte. Sie ist Alkoholikerin und schafft es nicht sich um ihre Kinder zu kümmern. Die 5 Kinder haben 3 unterschiedliche Väter und sehr starke Charaktere. Sie stehen alle auf der Adoptionsliste.</font></p>\r\n<p align=\"justify\"><font size=\"3\"><strong>Luis Alonso</strong> (6), Jolver (9), Florinda (11) und Sonia (13) wurden von ihrem Vater geschickt, der uns um Hilfe bat als seine Frau starb und er sich mit 6 Kindern alleine vorfand. Sie lebten unter sehr armen Umständen im Regenwald, wo der Vater es heute kaum schafft sich selbst zu ernähren. Luis Alonso kam mit vielen sehr schweren Krankheiten wie Anämie, Malaria und Hepatitis, war stark unterernährt und hat heute noch psychische Probleme. Für beide Brüder war es schwer den Tod ihrer Mutter zu überwinden und von ihren Geschwistern getrennt zu sein. Sie verweigerten anfangs jeden Kontakt, selbst zu ihrem Vater. Die beiden Schwestern kamen erst nach einem Jahr zu uns, in dem wir darum kämpften, sie alle wieder zusammen zu führen. Heute leben sie schon seit mehr als 4 Jahren bei uns und scheinen glücklich zu sein. Ihr Vater kommt sie besuchen, wenn er kann. Sie stehen nicht auf der Adoptionsliste.</font></p>\r\n<p align=\"justify\"><font size=\"3\">Der 6-jährige <strong>Alberto</strong> ist vor 3 Jahren zu uns gekommen, seine Schwester Ciliana (10) kam erst Anfang diesen Jahres zu uns. Alberto ist im Gefängnis geboren, wo seine Eltern sich heute immer noch wegen Drogenhandels befinden. Seine Schwester befand sich zu der Zeit schon in einem anderen Kinderheim. 2 Jahre lang musste Alberto im Gefängnis leben bis ein Unfall passierte, bei dem Alberto in einen Kessel mit hei&#946;em Wasser gefallen sein soll. Er kam sofort ins Krankenhaus, wo seine Brandnarben über einen Monat lang behandelt werden mussten. Anschlie&#946;end sollte er wieder zurück ins Gefängnis, weigerte sich aber zutiefst. Auch heute hat man manchmal noch den Eindruck, er stünde unter Schock. Seine Brandnarben sind verheilt, doch seine seelischen Wunden benötigen weiterhin sehr viel Pflege. </font></p>\r\n<p align=\"justify\"><font size=\"3\"><strong>Noemi </strong>(6) hat ebenfalls eine sehr erschreckende Familiengeschichte. Ihre Mutter verlor ihre gesamte Familie zur Zeit des Terrorismus und wuchs selbst bei Adoptiveltern auf. Sie war schwer traumatisiert, lebte mehr auf der Stra&#946;e als zu Hause und wurde selbst mit 15 Jahren von einem Unbekannten schwanger. Noemis ältere Schwester lebt ebenfalls in einem Kinderheim, nun bekam ihre Mutter ihr drittes Kind von einem dritten Mann. Noemi kam mit einer chronischen Unterernährung und konnte kaum gehen ohne ihr Gleichgewicht zu verlieren. Heute tanzt sie…gut, ab und zu fällt sie noch aber sie ist ein sehr fröhliches Kind und eins der Sonnenscheine unseres Hauses.</font></p>\r\n<p align=\"justify\"><font size=\"3\">Die kleine <strong>Nilda </strong>kam vor 1 ½ Jahren direkt aus dem Krankenhaus zu uns, in dem ihr aufgrund einer ernsthaften Lungenentzündung schon alle Überlebenschancen abgesprochen wurden. Hier kam sie dann mit einer zerebralen Lähmung, einer chronischen Unterernährung und einer Bronchopneumonie an, war stark dehydriert und wiegte mit einem Jahr und 8 Monaten gerade mal\r\n<st1:metricconverter productid=\"4,7 kg\">4,7 kg</st1:metricconverter>. Ihr Leben hing an einem seidigen Faden. Nildas Vater ist alkoholkrank und Analphabet. Das Geld, das er verdiente, gab er sofort zum Trinken aus. In der Familie wurde seitens des Vaters sehr viel Gewalt ausgeübt, die 8 Geschwister wurden von ihren Eltern völlig allein gelassen, nicht erzogen und haben nie viel Liebe erfahren. Die Mutter arbeitete auf einem kleinen Feld um ein wenig Gemüse mit nach Hause zu bringen. Nilda hat in ihrem kurzen Leben mehr Zeit im Krankenhaus als zu Hause verbracht. Doch dort wurde sie nicht gerne gesehen, denn für die Ärzte im Krankenhaus war sie ein hoffnungsloser Fall. Man solle sie sterben lassen, war der Ratschlag. Auch hier konnte sie nicht geheilt werden: Sie hatte oft Fieber, konnte die Nahrung nicht bei sich behalten und hatte Flüssigkeitsansammlungen um die Lunge herum. Wir brachten sie wieder ins Krankenhaus, wo es ihr langsam besser ging. Doch kurz vor ihrer Entlassung kam es zu einem Unfall, bei dem Nahrung in die Lungen der Kleinen geriet und sie beinahe erstickte. Sie musste reanimiert werden und schwebte eine Woche lang zwischen Leben und Tod. Doch Nilda hatte und hat einen sehr starken Überlebenswillen und kam wieder zu uns. Wir wissen nicht wie lange sie noch kämpfen kann aber wir begleiten sie auf ihrem Weg. Sie verzaubert mit ihrem wunderschönen Lächeln jede Señorita und jeden Freiwilligen.</font></p>\r\n<p align=\"justify\"><font size=\"3\"><strong>Sabina</strong> ist mit ihren 20 Jahren die älteste im Casa Los Gorriones. Sie ist schwerhörig und hat eine leichte geistige Behinderung. Wir wissen nicht viel über ihre Vergangenheit. Mit 10 Jahren wurde sie auf den Stra&#946;en von Lima gefunden und als Dienstmädchen eingestellt.\r\n<st1:place>Dort</st1:place> wurde sie sehr schlecht behandelt, musste hart arbeiten und wurde geschlagen. 4 Jahre später wurde Sabina zum Familienrichter gebracht, da die Frau, bei der Sabina arbeitete, verreisen wollte und Sabina nicht mehr gebrauchen konnte. Sie wurde nach Ayacucho gebracht, doch der Familienrichter wusste auch hier nichts mit ihr anzufangen. So kam sie zu uns. Sabina geht vormittags in eine Sonderschule und hilft uns nachmittags im Kinderheim. Sie fing an ein wenig zu sprechen und erlernte Handarbeiten. Sabina geht gerne zur Schule und macht Fortschritte. Sie scheint glücklich bei uns zu sein.</font></p>\r\n<p align=\"justify\"><font size=\"3\"><strong>Margary </strong>ist 13 Jahre alt und kam Anfang des Jahres 2003 zu uns. Sie wurde von ihrer älteren Schwester gebracht, die für ihre 4 kleineren Geschwister verantwortlich ist. Ihre Eltern waren Alkoholiker und starben kurz hintereinander. Margarys Brüder leben ebenfalls in einem Kinderheim. Margary hat ihr Elternhaus zuvor niemals verlassen und konnte nie zur Schule gehen. Sie hat niemals Liebe oder eine Erziehung erfahren. Sie ist geistig zurückgeblieben und hat gro&#946;e Probleme beim Sprechen. Anfangs war es kaum verständlich, was sie sagte. Au&#946;erdem hatte sie Angst vor Männern als sie ankam und versteckte ihr Spielzeug, weil sie nicht wusste, wie sie damit spielen sollte. Heute geht Margary zur gleichen Schule wie Sabina und macht gro&#946;e Fortschritte. Auch ihr scheint es sehr viel Spa&#946; zu machen. Sie ist ein fröhliches und aufgewecktes junges Mädchen. </font></p>\r\n<p align=\"justify\"><font size=\"3\">Der kleine 4-jährige <strong>Eberson </strong>kam im August 2004 zu uns. Er hat leider garkeine Angehörigen mehr. Seine Mutter starb bei seiner Geburt, so dass er von einer Familie aufgenommen wurde, die selbst 6 Kinder hatte. Doch auch hier starb die Mutter und so wurde Eberson zu uns gebracht. Eberson ist blind und hat eine zerebrale Lähmung…3 mal in der Woche fahren wir mit ihm zur Therapie. 2 mal täglich werden mit Eberson und allen anderen Kindern mit Behinderungen Rehabilitationsübungen gemacht. Er macht gute Fortschritte und wir hoffen, dass er bald laufen kann. Eberson kann sehr agressif sein und wirft gelegentlich seinen Kopf gegen die Mauer. Auf der anderen Seite ist er ein sehr liebebedürftiger und verschmuster Junge und braucht sehr viel Aufmerksamkeit. Eberson steht auf der Adoptionsliste.</font></p>\r\n<p align=\"justify\"><font size=\"3\"><strong>Diego </strong>(8) lebte mit seiner 35-jährigen Mutter in den Bergen, die den ganzen Tag arbeitete und ihren Sohn wie eine Pflanze in der Ecke stehen lie&#946;. Sein 65-jähriger Vater hat keinen Pfennig Geld und kein Haus, dafür aber unzählige Kinder, um die er sich nicht kümmert. So hatte Diego nicht die geringste Stimulation. Diese hätte er aber so dringend gebraucht, ist doch auch er ein Kind mit einer zerebralen Lähmung. Als Diego ankam, konnte er nicht einmal seine Augen öffnen. Nach einigen Monaten Rehabilitation kommuniziert er nun mit ein paar Lauten. Für uns ist das schon ein gro&#946;er Schritt.</font></p>\r\n<p align=\"justify\"><font size=\"3\"><strong>Yackner</strong> ist 5 Jahre alt und wurde von seinem Vater gebracht, der uns um Hilfe bat, weil seine Frau ihn angeblich verlassen hatte. Seit einigen Monaten ist die Mutter, die ihren Sohn nie besuchen kam, nun wegen Drogenhandels im Gefängnis. Der Vater und sein Sohn lebten in einem Haus ohne Elektrizität, ohne Wasser und ohne Toilette. Mit seinem behinderten Sohn konnte der Vater nicht arbeiten gehen. Yackner hat ebenfalls eine zerebrale Lähmung, starke Krämpfe und war unterernährt als er kam. Sein Vater kommt ihn nun jeden Sonntag besuchen und arbeitet heute als ausgezeichneter Bäcker. Man sieht, dass er seinen Sohn liebt. Wir versuchen ihm übergangsweise zu helfen und so, wie es aussieht, scheint es zu klappen. Er hat eine neue Familie gegründet und plant nun Yackner in einigen Monaten zu sich zu holen.</font></p>\r\n<p align=\"justify\"><font size=\"3\"><strong>Fermin </strong>ist 8 Jahre alt und kam im April diesen Jahres zu uns. Er ist ein ganz spezieller Junge, was sich unter anderem darin äu&#946;ert, dass er zwar schon 8 Jahre alt ist, im Grunde genommen aber aussieht wie ein Dreijähriger. Fermin hat eine starke geistige Behinderung und in seinem Leben nie eine Erziehung genossen. Er ist aus einer Vergewaltigung hervorgegangen; seine Eltern sind beide geistig behindert. In den ersten 6 Jahren lebte Fermin bei seiner Mutter. Diese wusste nicht, was sie mit ihrem Kind anfangen sollte und sperrte es deshalb nach Aussagen seiner Tante 6 Jahre lang in einem kleinen Zimmer ein.\r\n<st1:place>Dort</st1:place> wurde er so gut wie garnicht mit\r\n<st1:city>\r\n<st1:place>Essen</st1:place></st1:city> oder Kleidung versorgt, weshalb sein angeheirateter Gro&#946;vater ihn schlie&#946;lich zu seinem Vater schicken wollte. Doch auch dort war er nicht besser aufgehoben. Wir fanden ihn in einem stark vernachlässigten und verwahrlosten Zustand vor. Er hatte nichts weiter als einen Pullover an, sa&#946; an einem kalten Regentag barfu&#946; in einer dunklen Ecke und schien stark unterernährt zu sein. Er schien so einen Hunger zu haben, dass er sogar seine eigenen Exkremente ass. Heute bestehen Vermutungen, er habe eine Psychose. Auf der anderen Seite wirkt Fermin aber auch hyperaktiv, so dass noch nicht geklärt werden konnte, was er wirklich hat. Er braucht deshalb dringend professionelle Hilfe. Fermin fordert sehr viel Aufmerksamkeit, weil man ihn keine Sekunde aus den Augen lassen kann. Er kann keine Minute still sitzen, läuft ständig von einem Ort zum anderen, gibt einsilbige Laute wie ma-ma-ma-ma-ma von sich und nimmt alles in den Mund, was er finden kann. Auch seine Hände nimmt er andauernd in den Mund. Man kann sagen, dass der kleine Junge nichts tut, was einen Sinn ergeben würde. Er beisst, fügt anderen Schmerzen zu, ist aber auch autoagressif. Fermin hört so gut wie garnicht auf Anweisungen und reagiert selten auf seinen Namen. Es ist schwer zu sagen, wieviel er versteht, wie stark seine geistige Behinderung ist und wieviel Resultat einer ausbleibenden Erziehung und eines gewalttätigen familiären Umfelds ist. Wir hoffen mit bald folgenden Untersuchungen mehr &#971;ber seinen Zustand zu erfahren.</font></p>\r\n<p align=\"justify\"><font size=\"3\"><strong>Sheyla </strong>ist 4 Jahre alt und hat eine körperliche und eine leicht geistige Behinderung. Diese äu&#946;ert sich darin, dass sie nicht richtig sprechen kann und aufgrund einer H&#971;ftverrenkung Schwierigkeiten beim Gehen hat. Ihre Hüfte wurde mehrfach operiert. Des Weiteren hat Sheyla eine Elepsie. Ihre junge Mutter ist in der Ausbildung. In ihrer Vergangenheit hat sie keinerlei familiäre Unterst&#971;tzung zur Versorgung ihrer Tochter erfahren und hat deshalb um Hilfe im Casa Hogar gebeten. In ihrer Familie gab es einige Alkoholprobleme, zudem musste sie sich um ihre Lehre k&#971;mmern und ihren Lebensunterhalt verdienen. Sie kommt Sheyla jeden Sonntag besuchen oder holt sie zu Ausflügen ab. Sie liebt ihre Tochter und bemüht sich um ihr Wohlergehen. Es ist klar, dass sie Sheyla nach Hause holen möchte, sobald es möglich ist.</font></p>\r\n<p align=\"justify\"><font size=\"3\"><strong>Christofer </strong>ist mit seinen 1 ½ Jahren der Kleinste in unserem Haus. Er kam erst im August diesen Jahres aus einem anderen Kinderheim zu uns.\r\n<st1:place>Dort</st1:place> hatten sie nicht die Möglichkeit seine physomotorische Behinderung zu behandeln, die bei seiner Geburt entstand. Seine Mutter, die mental beeinträchtigt ist, befand sich gerade auf der Tanzfläche einer Diskothek als sie ihn empfing. Dabei fiel Christofer auf seine rechte Körperhälfte, die heute wöchentlich in der Physiotherapie behandelt wird. Er kann leider seine rechte Hand nicht bewegen und muss sie deshalb täglich trainieren. Er ist ein aufgewecktes kleines Baby und wird nach seiner Behandlung in unserer Einrichtung in das andere Kinderheim zur&#971;ckkehren.</font></p>\r\n<p align=\"left\">&nbsp;</p></td></tr></tbody></table>\r\n<div>&nbsp;</div>\r\n<div>&nbsp;</div>\r\n<div>&nbsp;</div>\r\n<div>&nbsp;</div>','','','','',null,null,'','','','',null,null,null,null);
