REPLACE INTO rvs_project_info (project_info_id,template_item_id,font_id,navigator_id,rvs_category_id,charset_id,project_company,project_slogan,project_logo,project_title,project_keyword,project_description,project_domain,check_step,effect_site_enter,duration_site_enter,last_update,layout_template_id,i_pathway,i_mail,i_print,i_favorite,no_slogan,no_company_name,navigator_name_id) VALUES ('1544b133e78d8647dc13cb5716452f62','2b776452743e97ac02615128e2f396ff','1','9b0b1404ad32f71de0aaa96598a4ffad','17','20','Casa Hogar Los Gorriones','','NOPIC','Casa Hogar Los Gorriones','Casa, hogar, los, gorriones, orphanage, sparrows, children, needs, special, poverty, ayacucho, volunteer, work, programs, donations, help, education, health, care, kuska, construction, house, sponsor, program, food, history,development, international.','The Casa hogar Los Gorriones is a small family oriented orphanage in the region of Ayacucho in Peru.Los Gorriones welcomes abandoned children from age 0 to 18.','casahogarlosgorriones.org','4',null,null,'1244323457','5','1','1','1','1','1','0','1');
REPLACE INTO rvs_project_page (project_page_id,rvs_usr_id,page_name,custom_page_name,rvs_component_id,page_data,head,page_title,meta_keyword,meta_description,effect_page_enter,duration_page_enter,meta_page_effect,rvs_link_id,target,form_template_id,i_pathway,i_mail,i_print,i_favorite) VALUES ('3d32b29078b1ac5131ed8e0c908022d8','4a9479c2086afe0578d281c45072190b','Programs & Donation','','0','<p><br />\r\n<!-- ####### LAYOUT CSS: MODIFY COLOR, FONT AND BOX SIZE HERE BUT DO NOT DELETE. ####### -->\r\n<style type=\"text/css\">\r\n.type15border_tbl{\r\n		border:#42778F solid 1px;\r\n		background-color:#FFFFFF;\r\n}\r\n.type15border_tbl02{border:#42778F solid 1px;}\r\n.type15head{\r\n		font-size:110%;\r\n		font-weight:bold;\r\n		color:#FFFFFF;\r\n		background-color:#5492AF;\r\n}\r\n.type15head02{\r\n		font-size:110%;\r\n		font-weight:bold;\r\n		color:#42778F;\r\n}\r\n.type15subhead{\r\n		padding:1px 8px;\r\n		font-size:90%;\r\n		background-color:#42778F;\r\n		color:#FFFFFF;\r\n		font-weight:bold;\r\n		width:50%;\r\n}\r\n.type15bgblue{\r\n		padding:5px;\r\n		border-left: #FFFFFF solid 1px;\r\n		background-color:#88B4C8;\r\n}\r\n.type15lineleft{border-left: #CBCBCB solid 1px;\r\n}\r\n.type15padbullet{padding:8px 5px 5px 20px;}\r\n.type15linetd{background-image:url(/3rdparty/rvsitebuilder/themes/default/sitebuilder/images/layouttemplate/c20a23a80aff1b47a8de9283c6b4ec5f/linetd.gif);}\r\n.type15paraleft{\r\n		padding:10px 10px;\r\n		text-align:left;\r\n}\r\n.type15padbody{\r\n		padding:3px 5px 15px 5px;\r\n		text-align:left;\r\n}\r\n.type15txtblue{\r\n		color:#42778F;\r\n		font-size:110%;\r\n		font-weight:bold;\r\n}\r\n.type15txtbold{font-weight:bold;}</style><!-- ########################### END CSS LAYOUT ###########################  -->\r\n<table cellspacing=\"2\" cellpadding=\"0\">\r\n<tbody>\r\n<tr>\r\n<td valign=\"top\" width=\"220\">\r\n<table cellspacing=\"0\" cellpadding=\"5\" width=\"100%\">\r\n<tbody>\r\n<tr>\r\n<td class=\"type15head02\" valign=\"top\">Hearing Aids-Thanks you!!</td></tr>\r\n<tr>\r\n<td valign=\"top\" align=\"left\">\r\n<div class=\"type15paraleft\">In December, a dad came to Los Gorriones seeking for help for his son.&nbsp; The young saoul need hearing Aids.&nbsp; Thanks to your generous responds, with your help, we were able to provide him with professional help and hearing aids!</div>\r\n<div class=\"type15paraleft\">The total cost was $ 1600 US, which coresponds to&nbsp;the entire salary of 1600&nbsp;days of work for a good worker in Ayacucho.&nbsp;On behalf of this family, Thank you!</div>\r\n<hr size=\"1\" /></td></tr>\r\n<tr>\r\n<td valign=\"top\" align=\"left\">\r\n<div class=\"type15subhead\"><font size=\"3\">Volunteers </font></div>\r\n<div class=\"type15paraleft\"><strong>Volunteers needed!!&nbsp; We are looking for dynamic volunteers.&nbsp; Please contact Don at: <a href=\"&#109;&#97;&#105;&#108;&#116;&#111;&#58;&#100;&#111;&#110;&#97;&#115;&#105;&#97;&#110;&#95;&#108;&#111;&#115;&#103;&#111;&#114;&#114;&#105;&#111;&#110;&#101;&#115;&#64;&#104;&#111;&#116;&#109;&#97;&#105;&#108;&#46;&#99;&#111;&#109;\">&#100;&#111;&#110;&#97;&#115;&#105;&#97;&#110;&#95;&#108;&#111;&#115;&#103;&#111;&#114;&#114;&#105;&#111;&#110;&#101;&#115;&#64;&#104;&#111;&#116;&#109;&#97;&#105;&#108;&#46;&#99;&#111;&#109;</a></strong>&nbsp;</div>\r\n<hr size=\"1\" /></td></tr>\r\n<tr>\r\n<td valign=\"top\" align=\"left\">\r\n<div class=\"type15subhead\"></div>\r\n<div class=\"type15paraleft\">&nbsp;</div></td></tr></tbody></table></td>\r\n<td valign=\"top\">\r\n<table cellspacing=\"0\" cellpadding=\"0\" width=\"100%\">\r\n<tbody>\r\n<tr>\r\n<td valign=\"top\">\r\n<table class=\"type15border_tbl\" cellspacing=\"8\" cellpadding=\"0\" width=\"100%\">\r\n<tbody>\r\n<tr>\r\n<td valign=\"top\" align=\"left\">\r\n<table cellspacing=\"0\" cellpadding=\"0\" width=\"100%\">\r\n<tbody>\r\n<tr>\r\n<td class=\"type15bgblue\" valign=\"top\">\r\n<div><font size=\"3\"><strong>Money Contribution</strong></font></div>\r\n<div>The activities of La Casa Hogar Los Gorriones depend entirely of&nbsp; private donations. For general activities, for education or for a special project that you find important, your donation is very important to us!! To make a direct donation by account transfer, credit card or Paypal, click here:&nbsp; <strong><font size=\"4\"><a title=\"\" href=\"http://casahogarlosgorriones.org/english/Donations.html\">DONATION</a></font></strong></div>\r\n<div>&nbsp;</div></td></tr></tbody></table></td></tr>\r\n<tr valign=\"top\" bgcolor=\"#7babc3\">\r\n<td valign=\"top\" align=\"left\">\r\n<div>&nbsp;<strong><font size=\"3\">Members - All Together!!!</font></strong></div>\r\n<div><font size=\"2\">Becoming a member is a way to belong to the friends of Los Gorriones and to encourage the center’s activities. By being a member, you receive a bi-monthly newsletter with the center’s latest news, the annual report and you will have the chance to share your ideas and comments with us concerning the center’s operation and projects. Becoming a member is above all a way to to bring together all the friends of Los Gorriones so as to support the projects more efficiently. Upon registering, you commit to giving $20 US per month for a twelve month period. You have the choice of having payments taken from your account or from your chosen card. To become a member. click here: </font><font size=\"4\"><strong><a title=\"\" href=\"http://casahogarlosgorriones.org/english/Donations.html\">MEMBER</a></strong></font></div>\r\n<div><font size=\"2\"></font>&nbsp;</div></td></tr></tbody></table></td></tr>\r\n<tr>\r\n<td height=\"1\"></td></tr>\r\n<tr>\r\n<td valign=\"top\">\r\n<table class=\"type15border_tbl\" cellspacing=\"8\" cellpadding=\"0\" width=\"100%\">\r\n<tbody>\r\n<tr>\r\n<td valign=\"top\" align=\"left\">\r\n<table cellspacing=\"0\" cellpadding=\"0\" width=\"100%\">\r\n<tbody>\r\n<tr>\r\n<td valign=\"top\" align=\"left\"></td>\r\n<td class=\"type15bgblue\" valign=\"top\"><strong><font size=\"3\">Education Sponsorship Program</font></strong>\r\n<div><font size=\"2\">The official sponsorship program was created by Kuska to ensure access to education to children living in poor conditions, including children with special needs. Since \r\n<st1:personname>2</st1:personname>007, all children of Los Gorriones have access to this program, which allows kids to be matched with a sponsor who commits to contributing in full to the child’s education fees. </font></div>\r\n<div><font size=\"2\"></font>&nbsp;</div>\r\n<div><font size=\"2\"><strong>Beneficiaries&nbsp; </strong></font></div>\r\n<div><font size=\"2\">All children of Los Gorriones, from pre-school age to kids with special needs (physical or mental illnesses). For Los Gorriones, education begins at birth; each and every child needs education in order to allow optimum development and to build a solid foundation towards physical and intellectual growth.&nbsp; Professional services and schools The majority of children attend private institutions in Ayacucho. Children with special needs who suffer mobility restraints receive professional services at the orphanage. Private tutoring is also provide in Los Gorriones.</font></div>\r\n<div><font size=\"2\"></font>&nbsp;</div>\r\n<div><font size=\"2\"><strong>Fees</strong></font></div>\r\n<div><font size=\"2\">The sponsoring program is structured around the annual tuition, transportation, school supplies and uniform required to attend private school, which comes up to approximately 600$US per year. A automatic pre-payment plan allows sponsors to make a 50$ monthly contribution by account transfer, credit card or through Paypal. A total lump sum contribution is also available if you so desire.</font></div>\r\n<div><font size=\"2\"></font>&nbsp;</div>\r\n<div><font size=\"2\"><strong>Sponsor</strong></font></div>\r\n<div><font size=\"2\">Sponsors are responsible to financially contribute to the child education for a period of one year.&nbsp; The sponsor will receive a descriptive file of the child they are sponsoring and photo provided by Kuska.</font></div>\r\n<div><font size=\"2\"></font>&nbsp;</div>\r\n<div><font size=\"2\"><strong>Correspondence</strong></font></div>\r\n<div><font size=\"2\">Throughout the year, the sponsor is encouraged to communicate through the web with Los Gorriones (directly with Gil) or Kuska (program contributor). The sponsor is also encouraged to send pictures and letters to the child. Reasonable gifts are also accepted. Los Gorriones will send out newsletters throughout the year and postal correspondence from Ayacucho quaterly. </font></div>\r\n<div><font size=\"2\"></font>&nbsp;</div>\r\n<div><font size=\"2\"><strong>Visiting Los Gorriones and your Sponsored Child</strong></font></div>\r\n<div><font size=\"2\">Los Gorriones is always open to visitors! Contact Gil and provide dates of your visit. Please note that Los Gorionnes is responsible for all children on the premise. Many of the kids have been abandoned and remain on the national adoption listing. Others have been voluntarily brought by their parents, who still remain the legal guardians. A sponsor contributes financially towards the child’s education. Building relationship with the child and his family is encouraged; however, a sponsor&nbsp;doesn\'t play the role of an adoptive parent. It is therefore against policy to have a child travel with his sponsor and/or visit his country.</font></div>\r\n<div>&nbsp;</div>\r\n<div>Since the launch of our sponsoring program, almost all the kids have been matched with a sponsor….however, there is still a short list that have not. Our objective is to have all children of Los Gorriones sponsored; in conjunction with Kuska, we wish to extend the sponsoring program to the kid in the neighborhood. We need your help!</div>\r\n<div>\r\n<p>For more information regarding our sponsor-schooling program, contact us at:<font size=\"4\"> </font><a href=\"&#109;&#97;&#105;&#108;&#116;&#111;&#58;&#105;&#110;&#102;&#111;&#64;&#99;&#97;&#115;&#97;&#104;&#111;&#103;&#97;&#114;&#108;&#111;&#115;&#103;&#111;&#114;&#114;&#105;&#111;&#110;&#101;&#115;&#46;&#111;&#114;&#103;\"><font size=\"4\">&#105;&#110;&#102;&#111;&#64;&#99;&#97;&#115;&#97;&#104;&#111;&#103;&#97;&#114;&#108;&#111;&#115;&#103;&#111;&#114;&#114;&#105;&#111;&#110;&#101;&#115;&#46;&#111;&#114;&#103;</font></a><font size=\"4\"> or </font><a href=\"&#109;&#97;&#105;&#108;&#116;&#111;&#58;&#105;&#110;&#102;&#111;&#64;&#107;&#117;&#115;&#107;&#97;&#46;&#111;&#114;&#103;\"><font size=\"4\">&#105;&#110;&#102;&#111;&#64;&#107;&#117;&#115;&#107;&#97;&#46;&#111;&#114;&#103;</font></a><font size=\"4\">.</font></p></div></td></tr></tbody></table></td></tr></tbody></table></td></tr>\r\n<tr>\r\n<td height=\"1\"><font color=\"#af5050\" size=\"5\"><strong><a id=\"Sponsor\" name=\"Sponsor\"><font color=\"#af5050\" size=\"5\"><strong></strong></font></a>Sponsors Needed!!</strong></font></td></tr>\r\n<tr>\r\n<td valign=\"top\">\r\n<table class=\"type15border_tbl02\" cellspacing=\"0\" cellpadding=\"0\" width=\"100%\">\r\n<tbody>\r\n<tr>\r\n<td valign=\"top\" width=\"33%\">\r\n<table cellspacing=\"0\" cellpadding=\"5\" width=\"100%\">\r\n<tbody>\r\n<tr>\r\n<td class=\"type15head\" valign=\"top\"><font color=\"#9b4b24\" size=\"3\">Eberson-Sponsored!&nbsp; Thanks!</font></td></tr>\r\n<tr>\r\n<td class=\"type15padbody\" valign=\"top\" align=\"left\" bgcolor=\"#f0f0f0\">\r\n<p align=\"justify\"><img title=\"\" style=\"width: 106px; margin-right: 5px; height: 107px\" height=\"107\" alt=\"\" width=\"106\" align=\"left\" src=\"/3rdparty/rvsitebuilder/project/e90aa99743e4c832c1da7f7fd0480040/images/Eberson.jpg\" />Eberson arrived at the house in August 2004. Unfortunately he has no family. His mother died when he was born and Eberson was taken in by another family with 6 children. His adoptive mother then died and then he was brought to the house. Eberson is blind and has a cerebral palsy. He goes for therapy 3 times a week. They help him to walk a bit, and are hoping that he’ll be able to do this on his own soon. He needs a lot of love and attention. He very affectionate and smiles a lot, but sometimes he can be very aggressive, and when he’s not happy, he bangs his head on the floor. He loves playing with a ball and can spend hours listening to music and loves being taken for walks by volunteers, in a manta on their backs. He is on the adoption list.</p></td></tr></tbody></table></td>\r\n<td class=\"type15linetd\"><img height=\"1\" alt=\"\" width=\"2\" src=\"/3rdparty/rvsitebuilder/themes/default/sitebuilder/images/layouttemplate/c20a23a80aff1b47a8de9283c6b4ec5f/spacer.gif\" /></td>\r\n<td valign=\"top\" width=\"34%\">\r\n<table cellspacing=\"0\" cellpadding=\"5\" width=\"100%\">\r\n<tbody>\r\n<tr>\r\n<td class=\"type15head\" valign=\"top\"><font size=\"3\">Yacner-Sponsored-March 2008-Thanks!</font></td></tr>\r\n<tr>\r\n<td class=\"type15padbody\" valign=\"top\" align=\"left\" bgcolor=\"#f0f0f0\">\r\n<div align=\"justify\">Yacner was brought in by his father seeking help, because his wife had left him<em>.&nbsp; </em>Father and son lived in a house without electricity, water, and a toilet.&nbsp; Yackner’s father was unable to go to work due to his son’s cerebral paralysis.&nbsp; Yackner arrived to the house undernourished and suffering from severe cramps.&nbsp; Today, his father works as an successful baker and visits his son at the Casa every Sunday.&nbsp; It is obvious to anyone that he loves his son very much.&nbsp; We have been trying to help Yackner’s father for some time and it seems to be working.&nbsp; He has a new family now and in a few months plans to take Yackner home again.&nbsp; His mother, on the other hand, never came to the Casa to visit Yackner and is now in prison on drug charges.</div>\r\n<div align=\"justify\">&nbsp;</div>\r\n<div align=\"justify\">&nbsp;</div>\r\n<div align=\"justify\">&nbsp;</div></td></tr></tbody></table></td>\r\n<td class=\"type15linetd\"><img height=\"1\" alt=\"\" width=\"2\" src=\"/3rdparty/rvsitebuilder/themes/default/sitebuilder/images/layouttemplate/c20a23a80aff1b47a8de9283c6b4ec5f/spacer.gif\" /></td>\r\n<td valign=\"top\" width=\"33%\">\r\n<table cellspacing=\"0\" cellpadding=\"5\" width=\"100%\">\r\n<tbody>\r\n<tr>\r\n<td class=\"type15head\" valign=\"top\"><font size=\"3\">Cristophe-Sponsored March 2008-Thanks!</font></td></tr>\r\n<tr>\r\n<td class=\"type15padbody\" valign=\"top\" align=\"left\" bgcolor=\"#f0f0f0\">\r\n<div>At the age of one and a half,<strong> Christofer<em> </em></strong>is the youngest in the Casa.&nbsp; He came to us in August of 2007 from another orphanage that could not treat his psychomotor disabilities.&nbsp; His mother, who is mentally impaired, was on the dance floor of a disco club when she went into labor.&nbsp; The delivery resulted in Christofer hitting the floor with the right side of his body.&nbsp; Currently he receives daily physical therapy to stimulate his extremities, especially his right hand.&nbsp; He has regained the movement of his right hand; however, he still does not use it.&nbsp;&nbsp; He is an alert and happy baby who will return to his previous orphanage after the completion of his treatment at the Casa.</div>\r\n<div>&nbsp;</div>\r\n<div>&nbsp;</div>\r\n<div>&nbsp;</div></td></tr></tbody></table></td></tr></tbody></table></td></tr></tbody></table></td></tr></tbody></table>\r\n<div>&nbsp;</div>\r\n<div>&nbsp;</div>\r\n<div>&nbsp;</div>','','Programs and Donations','','',null,null,'','','','',null,null,null,null);
REPLACE INTO rvs_project_page (project_page_id,rvs_usr_id,page_name,custom_page_name,rvs_component_id,page_data,head,page_title,meta_keyword,meta_description,effect_page_enter,duration_page_enter,meta_page_effect,rvs_link_id,target,form_template_id,i_pathway,i_mail,i_print,i_favorite) VALUES ('ab94e8ba95293d4c36f32ed6a12a478e','4a9479c2086afe0578d281c45072190b','Los Gorriones','','0','<div align=\"center\">&nbsp;</div><!-- ####### LAYOUT CSS: MODIFY COLOR, FONT AND BOX SIZE HERE BUT DO NOT DELETE. ####### -->\r\n<style type=\"text/css\">\r\n.ttype03head{\r\n	font-size:120%;\r\n	color: #000000;\r\n	font-weight:bold;\r\n	text-align:left;\r\n	padding:5px 0px;\r\n}\r\n.ttype03subhead{\r\n	font-weight:bold;\r\n	text-align:left;\r\n}\r\n#ttype03line{\r\n	border-bottom: #B8B8B8 dotted 2px;\r\n	padding-bottom: 5px;\r\n}</style><!-- ########################### END CSS LAYOUT ###########################  -->\r\n<table style=\"width: 961px; height: 1045px\" cellspacing=\"0\" cellpadding=\"3\" width=\"961\">\r\n<tbody>\r\n<tr>\r\n<td class=\" wproHighlight\" valign=\"top\" align=\"left\">\r\n<div class=\"ttype03head\">\r\n<table style=\"width: 6.83%; border-collapse: collapse; height: 38px\" border=\"0\" cellspacing=\"0\" bordercolor=\"#ffffff\" cellpadding=\"3\" align=\"right\">\r\n<tbody>\r\n<tr valign=\"top\">\r\n<td>\r\n<h2>&nbsp;<a title=\"\" href=\"http://casahogarlosgorriones.org/Deutsch/\"><img title=\"\" border=\"0\" alt=\"\" width=\"50\" height=\"33\" src=\"/3rdparty/rvsitebuilder/project/e90aa99743e4c832c1da7f7fd0480040/images/allemand.jpg\" /></a></h2></td>\r\n<td>\r\n<h2>&nbsp;<a title=\"\" href=\"http://casahogarlosgorriones.org/Espanol/\"><img title=\"\" border=\"0\" alt=\"\" width=\"50\" height=\"33\" src=\"/3rdparty/rvsitebuilder/project/e90aa99743e4c832c1da7f7fd0480040/images/espagnol.jpg\" /></a></h2></td>\r\n<td>\r\n<h2>&nbsp;<a title=\"\" href=\"http://casahogarlosgorriones.org\"><img title=\"\" border=\"0\" alt=\"\" width=\"50\" height=\"33\" src=\"/3rdparty/rvsitebuilder/project/e90aa99743e4c832c1da7f7fd0480040/images/french.jpg\" /></a></h2></td></tr></tbody></table><font color=\"#40bf40\" size=\"6\"><strong>**<a title=\"\" target=\"_blank\" href=\"ce0f635d3afd33da468fde40ea6e61a3.html\">Cycling Arthur\'s Seat</a>**</strong></font></div></td></tr>\r\n<tr>\r\n<td class=\" wproHighlight\" valign=\"top\" width=\"99%\" align=\"left\">&nbsp;\r\n<div>\r\n<div class=\"type12txthead\"><font color=\"#9b4b24\" size=\"7\">Casa Hogar Los Gorriones</font></div>\r\n<div class=\"type12txthead\"><font size=\"6\">The children\'s house&nbsp;«The Sparrow» </font></div>\r\n<p class=\"type12txthead\" align=\"justify\"><font size=\"3\"><img style=\"width: 223px; margin-bottom: 0px; height: 167px; margin-right: 3px\" title=\"\" alt=\"\" align=\"left\" width=\"223\" height=\"167\" src=\"/3rdparty/rvsitebuilder/project/e90aa99743e4c832c1da7f7fd0480040/images/casaweb.jpg\" />This is Ayacucho, one of the poorest cities in one of the poorest countries.&nbsp; This is where the infamous terrorist organization «The Shinig Path» (Sendero luminoso) was born and violently ruled the area during the years 80-90.&nbsp; Today, this city, although quit charming and tremendously welcoming, is home of thousands of workless peasants who fled the miseries of the campos.&nbsp; We\'re talking unemployement rate of 80%...But what does this means in reality?&nbsp; The intensity of the mid-day sun is unbearable through the ever-growing shantytowns.&nbsp; An Older lady is spending countless hours at the local market selling clover for less than a dollar a day...A moto-taxi driver goes home after is daily <?xml:namespace prefix = st1>\r\n1</st1:personname>5 hours shift knowing he won\'t be able to afford sending his kids to school...We are far from the golden empire of the Incas!&nbsp; Beyon\r\n\r\nPeru</st1:place></st1:country-region>\'s vibrant culture and heritage, a large number of inhabitants share this same destiny.&nbsp; Faced with these challenges, many children are suffering.&nbsp; Alcoholism and violence are gaining ground in the households and prostitution has become abundant in the streets.&nbsp; For many parents, the only hopes of seeing their children survive come downs to abandoning them on the street.&nbsp; Orphanage and public institutions are rare and the ones in operation are over crowded. </font>\r\n<div class=\"type12txthead\" align=\"justify\"><font size=\"3\">In\r\n2</st1:personname>00\r\n1</st1:personname>, Gil, Chantal and their son Aaron arrived in\r\n\r\nPeru</st1:place></st1:country-region>. He\'s from\r\n\r\nBelgium</st1:place></st1:country-region>; she\'s from France, both in their fifties.&nbsp; In their suitcase, tons of love and happiness to share and the example of Mother Theresa of\r\n\r\nCalcutta</st1:place></st1:city>....</font></div>\r\n<div class=\"type12txthead\" align=\"justify\"><font size=\"3\"></font>&nbsp;<br />\r\n<!-- ####### LAYOUT CSS: MODIFY COLOR, FONT AND BOX SIZE HERE BUT DO NOT DELETE. ####### -->\r\n<style type=\"text/css\">\r\n\r\ntable {\r\n\r\n	font-size:100%;\r\n\r\n	color:#535353;\r\n\r\n	font-family:Verdana, Arial, Helvetica, sans-serif;\r\n\r\n	}\r\n\r\nbody {\r\n\r\n	font-size:80%;\r\n\r\n	color:#535353;\r\n\r\n	margin:0;\r\n\r\n	padding:0;\r\n\r\n}\r\n\r\n.headerMaroon {\r\n\r\n	color:#710909;\r\n\r\n	background-color:#F7EAEA;\r\n\r\n	font-weight:bold;\r\n\r\n	font-size:110%;\r\n\r\n	padding:3px 0 5px 0;\r\n\r\n}\r\n\r\n.framePic {\r\n\r\n	border:#E0E0E0 1px solid;\r\n\r\n	padding:10px;\r\n\r\n	text-align:center;\r\n\r\n	vertical-align:top;\r\n\r\n	height:117px;\r\n\r\n	width:80px;\r\n\r\n}\r\n\r\n.framePic div.name {\r\n\r\n	color:#4F4D4D;\r\n\r\n	font-weight:bold;\r\n\r\n	padding:5px 0 0px 0px;\r\n\r\n}\r\n\r\n.rightFrame {\r\n\r\n	background-image:url(/3rdparty/rvsitebuilder/themes/default/sitebuilder/images/layouttemplate/9dd12b138426f964f6fd2f38525976f9/rightFrame.jpg);\r\n\r\n	background-repeat:no-repeat;\r\n\r\n	background-position:top left;\r\n\r\n	width