REPLACE INTO rvs_project_page (project_page_id,rvs_usr_id,page_name,custom_page_name,rvs_component_id,page_data,head,page_title,meta_keyword,meta_description,effect_page_enter,duration_page_enter,meta_page_effect,rvs_link_id,target,form_template_id,i_pathway,i_mail,i_print,i_favorite) VALUES ('ce0f635d3afd33da468fde40ea6e61a3','4a9479c2086afe0578d281c45072190b','Arthur\'sSeat','','','<h1 align=\"center\"><font color=\"#cf3030\">24hrs Cycling around Arthur\'s Seat, Edinburgh, Scotland for Los Gorriones</font></h1>\r\n<div>&nbsp;</div><!-- ####### LAYOUT CSS: MODIFY COLOR, FONT AND BOX SIZE HERE BUT DO NOT DELETE. ####### -->\r\n<style type=\"text/css\">\r\n.ttype01head{\r\nfont-size:120%;\r\nfont-weight:bold;\r\npadding:10px 5px;\r\n}\r\n.ttype01pad{\r\npadding:0px 5px 10px 5px;\r\n}</style><!-- ########################### END CSS LAYOUT ########################### -->\r\n<div>\r\n<table cellspacing=\"0\" cellpadding=\"0\" width=\"100%\">\r\n<tbody>\r\n<tr>\r\n<td valign=\"top\" align=\"left\"><img style=\"margin-bottom: 2px; margin-right: 3px\" title=\"\" border=\"3\" alt=\"\" align=\"left\" width=\"150\" height=\"113\" src=\"/3rdparty/rvsitebuilder/project/e90aa99743e4c832c1da7f7fd0480040/images/arthursseat2.jpg\" /></td>\r\n<td valign=\"top\" width=\"99%\" align=\"left\">\r\n<div class=\"ttype01head\"><font size=\"4\">July 18th &amp; 19th 2009</font></div>\r\n<p class=\"ttype01pad\"><font size=\"3\">Drew Sharkey will be cycling round this hilly 3 miles circuit for 24 hours on&nbsp;July 18th/19th on behalf of&nbsp;Los Gorriones.&nbsp; Drew will hopefully cover around 100 laps and 300 miles in 24hrs hoping that this challenging goal will get anough attention to help raise money for&nbsp;the children of Los Gorriones.</font>&nbsp;</p>\r\n<h2 align=\"center\"><font color=\"#0055ff\">Support Drew\'s challenge by sponsoring him </font><font color=\"#0055ff\"></h2>\r\n<h2>\r\n<form method=\"post\" action=\"https://www.paypal.com/cgi-bin/webscr\">\r\n<p align=\"center\"><input value=\"_s-xclick\" type=\"hidden\" name=\"cmd\" /> <input value=\"5926863\" type=\"hidden\" name=\"hosted_button_id\" /> </p>\r\n<p align=\"center\"><input border=\"0\" alt=\"PayPal - The safer, easier way to pay online!\" type=\"image\" name=\"submit\" src=\"https://www.paypal.com/en_US/i/btn/btn_donateCC_LG.gif\" /></p> <img border=\"0\" alt=\"\" width=\"1\" height=\"1\" src=\"https://www.paypal.com/en_US/i/scr/pixel.gif\" /> </form></h2></font></td></tr></tbody></table></div>\r\n<div><!-- ########################### END CSS LAYOUT ########################### -->\r\n<table cellspacing=\"0\" cellpadding=\"3\" width=\"100%\">\r\n<tbody>\r\n<tr>\r\n<td class=\"ttype04head\" valign=\"top\" colspan=\"2\" align=\"left\"></td></tr>\r\n<tr>\r\n<td class=\"ttype04subhead\" valign=\"top\" align=\"left\"><font size=\"4\"><strong>Arthur\'s Seat, Edinburgh</strong></font></td>\r\n<td class=\"ttype04head\" valign=\"top\" rowspan=\"2\" align=\"left\"><font size=\"3\"><img title=\"\" border=\"3\" alt=\"\" width=\"140\" height=\"105\" src=\"/3rdparty/rvsitebuilder/project/e90aa99743e4c832c1da7f7fd0480040/images/arthursseat.jpg\" /></font></td></tr>\r\n<tr>\r\n<td valign=\"top\" align=\"left\"><font size=\"3\">Arthur\'s Seat is an extinct volcano, which erupted around 340 million years ago. At that time, Scotland was a very different place, located close to the equator. The rocks of Holyrood Park give us some clues about what it was like in the past - there are sandstones formed in warm shallow seas, layers of volcanic ash created by many explosive eruptions of the ancient volcano, and lava flows representing quieter times. The volcano has been eroded so that now less than half of it remains, and this allows us to walk into the heart of the volcano and see rocks created underground. </font></td></tr></tbody></table></div>\r\n<div><br />\r\n<!-- ####### LAYOUT CSS: MODIFY COLOR, FONT AND BOX SIZE HERE BUT DO NOT DELETE. ####### -->\r\n<style type=\"text/css\">\r\n.ttype01head{\r\n	font-size:120%;\r\n	font-weight:bold;\r\n	padding:10px 5px;\r\n}\r\n.ttype01pad{\r\n	padding:0px 5px 10px 5px;\r\n}</style><!-- ########################### END CSS LAYOUT ###########################  -->\r\n<table cellspacing=\"0\" cellpadding=\"0\" width=\"100%\">\r\n<tbody>\r\n<tr>\r\n<td valign=\"top\" align=\"left\"><img style=\"margin-bottom: 3px; margin-right: 3px\" title=\"\" border=\"3\" alt=\"\" align=\"left\" width=\"301\" height=\"200\" src=\"/3rdparty/rvsitebuilder/project/e90aa99743e4c832c1da7f7fd0480040/images/drew.jpg\" /></td>\r\n<td valign=\"top\" width=\"99%\" align=\"left\">\r\n<div class=\"ttype01head\"><font size=\"4\">Drew Sharkey</font></div>\r\n<div class=\"ttype01pad\"><font size=\"3\">Ma quande lingues coalesce, li grammatica del resultant lingue es plu simplic e regulari quam ti del coalescent lingues. Li nov lingua franca va esser plu simplic e regulari quam li existent Europan lingues. It va esser tam simplic quam Occidental: in fact, it va esser Occidental. A un Angleso it va semblar un simplificat Angles, quam un skeptic Cambridge amico dit me que Occidental es.</font></div></td></tr></tbody></table></div>\r\n<div>&nbsp;</div>','','','','',null,null,'','','','',null,null,null,null);
