REPLACE INTO rvs_project_page (project_page_id,rvs_usr_id,page_name,custom_page_name,rvs_component_id,page_data,head,page_title,meta_keyword,meta_description,effect_page_enter,duration_page_enter,meta_page_effect,rvs_link_id,target,form_template_id,i_pathway,i_mail,i_print,i_favorite) VALUES ('1d1eaff78c101ae88cc0c9c0c5d37498','4a9479c2086afe0578d281c45072190b','Accueil','','0','<div align=\"center\">&nbsp; <br />\r\n<!-- ####### LAYOUT CSS: MODIFY COLOR, FONT AND BOX SIZE HERE BUT DO NOT DELETE. ####### -->\r\n<style type=\"text/css\">\r\n.ttype03head{\r\nfont-size:120%;\r\ncolor: #000000;\r\nfont-weight:bold;\r\ntext-align:left;\r\npadding:5px 0px;\r\n}\r\n.ttype03subhead{\r\nfont-weight:bold;\r\ntext-align:left;\r\n}\r\n#ttype03line{\r\nborder-bottom: #B8B8B8 dotted 2px;\r\npadding-bottom: 5px;\r\n}</style><!-- ########################### END CSS LAYOUT ########################### -->\r\n<table style=\"width: 975px; height: 473px\" cellspacing=\"0\" cellpadding=\"3\" width=\"975\">\r\n<tbody>\r\n<tr>\r\n<td valign=\"top\" align=\"left\">\r\n<h1 class=\"ttype03head\"><font color=\"#bf4040\" size=\"6\"><strong></strong></font>&nbsp;\r\n<table style=\"width: 0.36%; height: 64px\" bordercolor=\"#000000\" cellspacing=\"0\" cellpadding=\"3\" align=\"right\" summary=\"\" border=\"0\">\r\n<tbody>\r\n<tr valign=\"top\">\r\n<td>\r\n<h2>&nbsp;<a title=\"\" href=\"http://www.casahogarlosgorriones.org/english/\"><img title=\"\" height=\"42\" alt=\"\" width=\"60\" border=\"0\" src=\"/3rdparty/rvsitebuilder/project/af4ed5d72f832457ceccee116779a05f/images/anglais.gif\" /></a></h2></td>\r\n<td>\r\n<h2>&nbsp;<a title=\"\" href=\"http://casahogarlosgorriones.org/Espanol/\"><img title=\"\" height=\"42\" alt=\"\" width=\"64\" border=\"0\" src=\"/3rdparty/rvsitebuilder/project/af4ed5d72f832457ceccee116779a05f/images/espagnol.jpg\" /></a></h2></td>\r\n<td>\r\n<h2>&nbsp;<a title=\"\" href=\"http://casahogarlosgorriones.org/Deutsch/\"><img title=\"\" height=\"42\" alt=\"\" width=\"64\" border=\"0\" src=\"/3rdparty/rvsitebuilder/project/af4ed5d72f832457ceccee116779a05f/images/allemand.jpg\" /></a></h2></td></tr></tbody></table></h1>\r\n<h1 class=\"ttype03head\"><font color=\"#bf4040\" size=\"6\"><strong>La Casa Hogar Los Gorriones</strong></font></h1>\r\n<div><font size=\"5\">La maison des enfants &quot; Les Moineaux&quot;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;<font color=\"#cf3030\">&nbsp; </font></font></div></td></tr>\r\n<tr>\r\n<td valign=\"top\" align=\"left\" width=\"99%\" bgcolor=\"#ffffff\">\r\n<div align=\"justify\"><span class=\"ttype03subhead\"><font face=\"Arial\"><img title=\"\" style=\"margin: 2px 5px 0px 2px; width: 250px; height: 192px\" height=\"192\" alt=\"\" width=\"250\" align=\"left\" src=\"/3rdparty/rvsitebuilder/project/af4ed5d72f832457ceccee116779a05f/images/IMGP1398.jpg\" /><font size=\"3\">Nous sommes à Ayacucho, une cité au coeur des Andes péruviennes, à 450 Km au sud de la capitale Lima.&nbsp; L\'une des villes les plus pauvres d\'un des pays les plus pauvres.&nbsp; C\'est ici que s\'est formé le mouvement terroriste <em>Le Sentier Lumineux (Sendero luminoso)</em> qui fit de cette région l\'une des plus dangereuses des années 80-90.&nbsp;&nbsp;Aujourd\'hui, cette ville, pourtant très charmante et chaleureuse, est&nbsp;habitée de villageois sans travail ayant fui les <em>campos.</em>&nbsp; On parle ici de 80% de chômage. Mais que cela signifie vraiment? Le soleil écrase les maisons aux toits de tôle et les vielles dames vendent du trèfle au marché pour gagner à peine 1$ par jour. Que dire de ce chauffeur de moto-taxi qui travaille 15 heures par jour et qui ne peut se permettre de payer l\'éducation de ses enfants?&nbsp; C\'est pas le Pérou! Pourtant nous y sommes. Au-delà de ses merveilles, le Pérou est un pays où de tels destins s\'écrivent par millions.&nbsp;Dans ces conditions, nombreux sont les enfants qui souffrent. L\'alcoolisme et la violence font des ravages dans les foyers et la prostitution envahie les rues. Pour plusieurs parents, le seul espoir de voir survivre leurs enfants est de les abandonner à eux-mêmes.&nbsp; Les maisons d\'accueil et orphelinats sont rares et ceux qui existent sont bondés.</font></font></span></div>\r\n<div align=\"justify\"><span class=\"ttype03subhead\"><font face=\"Arial\" size=\"3\">&nbsp; </font></span></div>\r\n<div align=\"justify\"><span class=\"ttype03subhead\"><font face=\"Arial\" size=\"3\">En 2001, Gil, Chantal et leur fils Aaron arrivent au Pérou.&nbsp; Il est Belge, elle est Française, ils ont la cinquantaine.&nbsp; Dans leurs bagages, de l\'amour et du bonheur à partager, et l\'exemple de Mère Thérésa de Calcuta...</font></span></div></td></tr></tbody></table></div>\r\n<div>&nbsp;<br />\r\n<!-- ####### LAYOUT CSS: MODIFY COLOR, FONT AND BOX SIZE HERE BUT DO NOT DELETE. ####### -->\r\n<style type=\"text/css\">\r\n\r\ntable {\r\n\r\nfont-size:100%;\r\n\r\ncolor:#535353;\r\n\r\nfont-family:Verdana, Arial, Helvetica, sans-serif;\r\n\r\n}\r\n\r\nbody {\r\n\r\nfont-size:80%;\r\n\r\ncolor:#535353;\r\n\r\nmargin:0;\r\n\r\npadding:0;\r\n\r\n}\r\n\r\n.headerMaroon {\r\n\r\ncolor:#710909;\r\n\r\nbackground-color:#F7EAEA;\r\n\r\nfont-weight:bold;\r\n\r\nfont-size:110%;\r\n\r\npadding:3px 0 5px 0;\r\n\r\n}\r\n\r\n.framePic {\r\n\r\nborder:#E0E0E0 1px solid;\r\n\r\npadding:10px;\r\n\r\ntext-align:center;\r\n\r\nvertical-align:top;\r\n\r\nheight:117px;\r\n\r\nwidth:80px;\r\n\r\n}\r\n\r\n.framePic div.name {\r\n\r\ncolor:#4F4D4D;\r\n\r\nfont-weight:bold;\r\n\r\npadding:5px 0 0px 0px;\r\n\r\n}\r\n\r\n.rightFrame {\r\n\r\nbackground-image:url(/3rdparty/rvsitebuilder/themes/default/sitebuilder/images/layouttemplate/9dd12b138426f964f6fd2f38525976f9/rightFrame.jpg);\r\n\r\nbackground-repeat:no-repeat;\r\n\r\nbackground-position:top left;\r\n\r\nwidth:24%;\r\n\r\n}\r\n\r\n.bottomFrame {\r\n\r\nbackground-image:url(/3rdparty/rvsitebuilder/themes/default/sitebuilder/images/layouttemplate/9dd12b138426f964f6fd2f38525976f9/bottomFrame.jpg);\r\n\r\nbackground-repeat:no-